    "surah_number": 26,
    "verse_number": 74,
    "content":
        "انہوں نے کہا (نہیں) بلکہ ہم نے اپنے باپ دادا کو اسی طرح کرتے دیکھا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 75,
    "content": "ابراہیم نے کہا کیا تم نے دیکھا کہ جن کو تم پوجتے رہے ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 76,
    "content": "تم بھی اور تمہارے اگلے باپ دادا بھی"
  },
  {
    "surah_number": 26,
    "verse_number": 77,
    "content": "وہ میرے دشمن ہیں۔ مگر خدائے رب العالمین (میرا دوست ہے)"
  },
  {
    "surah_number": 26,
    "verse_number": 78,
    "content": "جس نے مجھے پیدا کیا ہے اور وہی مجھے رستہ دکھاتا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 79,
    "content": "اور وہ جو مجھے کھلاتا اور پلاتا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 80,
    "content": "اور جب میں بیمار پڑتا ہوں تو مجھے شفا بخشتا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 81,
    "content": "اور جو مجھے مارے گا اور پھر زندہ کرے گا"
  },
  {
    "surah_number": 26,
    "verse_number": 82,
    "content":
        "اور وہ جس سے میں امید رکھتا ہوں کہ قیامت کے دن میرے گناہ بخشے گا"
  },
  {
    "surah_number": 26,
    "verse_number": 83,
    "content": "اے پروردگار مجھے علم ودانش عطا فرما اور نیکوکاروں میں شامل کر"
  },
  {
    "surah_number": 26,
    "verse_number": 84,
    "content": "اور پچھلے لوگوں میں میرا ذکر نیک (جاری) کر"
  },
  {
    "surah_number": 26,
    "verse_number": 85,
    "content": "اور مجھے نعمت کی بہشت کے وارثوں میں کر"
  },
  {
    "surah_number": 26,
    "verse_number": 86,
    "content": "اور میرے باپ کو بخش دے کہ وہ گمراہوں میں سے ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 87,
    "content": "اور جس دن لوگ اٹھا کھڑے کئے جائیں گے مجھے رسوا نہ کیجیو"
  },
  {
    "surah_number": 26,
    "verse_number": 88,
    "content": "جس دن نہ مال ہی کچھ فائدہ دے سکا گا اور نہ بیٹے"
  },
  {
    "surah_number": 26,
    "verse_number": 89,
    "content": "ہاں جو شخص خدا کے پاس پاک دل لے کر آیا (وہ بچ جائے گا)"
  },
  {
    "surah_number": 26,
    "verse_number": 90,
    "content": "اور بہشت پرہیزگاروں کے قریب کردی جائے گی"
  },
  {
    "surah_number": 26,
    "verse_number": 91,
    "content": "اور دوزخ گمراہوں کے سامنے لائی جائے گی"
  },
  {
    "surah_number": 26,
    "verse_number": 92,
    "content": "اور ان سے کہا جائے گا کہ جن کو تم پوجتے تھے وہ کہاں ہیں؟"
  },
  {
    "surah_number": 26,
    "verse_number": 93,
    "content":
        "یعنی جن کو خدا کے سوا (پوجتے تھے) کیا وہ تمہاری مدد کرسکتے ہیں یا خود بدلہ لے سکتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 94,
    "content":
        "تو وہ اور گمراہ (یعنی بت اور بت پرست) اوندھے منہ دوزخ میں ڈال دیئے جائیں گے"
  },
  {
    "surah_number": 26,
    "verse_number": 95,
    "content": "اور شیطان کے لشکر سب کے سب (داخل جہنم ہوں گے)"
  },
  {
    "surah_number": 26,
    "verse_number": 96,
    "content": "وہ آپس میں جھگڑیں گے اور کہیں گے"
  },
  {
    "surah_number": 26,
    "verse_number": 97,
    "content": "کہ خدا کی قسم ہم تو صریح گمراہی میں تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 98,
    "content": "جب کہ تمہیں (خدائے) رب العالمین کے برابر ٹھہراتے تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 99,
    "content": "اور ہم کو ان گنہگاروں ہی نے گمراہ کیا تھا"
  },
  {
    "surah_number": 26,
    "verse_number": 100,
    "content": "تو (آج) نہ کوئی ہمارا سفارش کرنے والا ہے"
  },
  {"surah_number": 26, "verse_number": 101, "content": "اور نہ گرم جوش دوست"},
  {
    "surah_number": 26,
    "verse_number": 102,
    "content": "کاش ہمیں (دنیا میں) پھر جانا ہو تم ہم مومنوں میں ہوجائیں"
  },
  {
    "surah_number": 26,
    "verse_number": 103,
    "content": "بےشک اس میں نشانی ہے اور ان میں اکثر ایمان لانے والے نہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 104,
    "content": "اور تمہارا پروردگار تو غالب اور مہربان ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 105,
    "content": "قوم نوح نے بھی پیغمبروں کو جھٹلایا"
  },
  {
    "surah_number": 26,
    "verse_number": 106,
    "content": "جب ان سے ان کے بھائی نوح نے کہا کہ تم ڈرتے کیوں نہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 107,
    "content": "میں تو تمہارا امانت دار ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 108,
    "content": "تو خدا سے ڈرو اور میرا کہا مانو"
  },
  {
    "surah_number": 26,
    "verse_number": 109,
    "content":
        "اور اس کام کا تم سے کچھ صلہ نہیں مانگتا۔ میرا صلہ تو خدائے رب العالمین ہی پر ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 110,
    "content": "تو خدا سے ڈرو اور میرے کہنے پر چلو"
  },
  {
    "surah_number": 26,
    "verse_number": 111,
    "content":
        "وہ بولے کہ کیا ہم تم کو مان لیں اور تمہارے پیرو تو رذیل لوگ ہوتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 112,
    "content": "نوح نے کہا کہ مجھے کیا معلوم کہ وہ کیا کرتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 113,
    "content": "ان کا حساب (اعمال) میرے پروردگار کے ذمے ہے کاش تم سمجھو"
  },
  {
    "surah_number": 26,
    "verse_number": 114,
    "content": "اور میں مومنوں کو نکال دینے والا نہیں ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 115,
    "content": "میں تو صرف کھول کھول کر نصیحت کرنے والا ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 116,
    "content": "انہوں نے کہا کہ نوح اگر تم باز نہ آؤ گے تو سنگسار کردیئے جاؤ گے"
  },
  {
    "surah_number": 26,
    "verse_number": 117,
    "content": "نوح نے کہا کہ پروردگار میری قوم نے تو مجھے جھٹلا دیا"
  },
  {
    "surah_number": 26,
    "verse_number": 118,
    "content":
        "سو تو میرے اور ان کے درمیان ایک کھلا فیصلہ کردے اور مجھے اور جو میرے ساتھ ہیں ان کو بچا لے"
  },
  {
    "surah_number": 26,
    "verse_number": 119,
    "content":
        "پس ہم نے ان کو اور جو ان کے ساتھ کشتی میں سوار تھے، ان کو بچا لیا"
  },
  {
    "surah_number": 26,
    "verse_number": 120,
    "content": "پھر اس کے بعد باقی لوگوں کو ڈبو دیا"
  },
  {
    "surah_number": 26,
    "verse_number": 121,
    "content": "بےشک اس میں نشانی ہے اور ان میں اکثر ایمان لانے والے نہیں تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 122,
    "content": "اور تمہارا پروردگار تو غالب (اور) مہربان ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 123,
    "content": "عاد نے بھی پیغمبروں کو جھٹلایا"
  },
  {
    "surah_number": 26,
    "verse_number": 124,
    "content": "جب ان سے ان کے بھائی ہود نے کہا کیا تم ڈرتے نہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 125,
    "content": "میں تو تمہارا امانت دار پیغمبر ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 126,
    "content": "تو خدا سے ڈرو اور میرا کہا مانو"
  },
  {
    "surah_number": 26,
    "verse_number": 127,
    "content":
        "اور میں اس کا تم سے کچھ بدلہ نہیں مانگتا۔ میرا بدلہ (خدائے) رب العالمین کے ذمے ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 128,
    "content": "بھلا تم ہر اونچی جگہ پر نشان تعمیر کرتے ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 129,
    "content": "اور محل بناتے ہو شاید تم ہمیشہ رہو گے"
  },
  {
    "surah_number": 26,
    "verse_number": 130,
    "content": "اور جب (کسی کو) پکڑتے ہو تو ظالمانہ پکڑتے ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 131,
    "content": "تو خدا سے ڈرو اور میری اطاعت کرو"
  },
  {
    "surah_number": 26,
    "verse_number": 132,
    "content": "اور اس سے جس نے تم کو ان چیزوں سے مدد دی جن کو تم جانتے ہو۔ ڈرو"
  },
  {
    "surah_number": 26,
    "verse_number": 133,
    "content": "اس نے تمہیں چارپایوں اور بیٹوں سے مدد دی"
  },
  {
    "surah_number": 26,
    "verse_number": 134,
    "content": "اور باغوں اور چشموں سے"
  },
  {
    "surah_number": 26,
    "verse_number": 135,
    "content": "مجھ کو تمہارے بارے میں بڑے (سخت) دن کے عذاب کا خوف ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 136,
    "content": "وہ کہنے لگے کہ ہمیں خواہ نصیحت کرو یا نہ کرو ہمارے لئے یکساں ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 137,
    "content": "یہ تو اگلوں ہی کے طریق ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 138,
    "content": "اور ہم پر کوئی عذاب نہیں آئے گا"
  },
  {
    "surah_number": 26,
    "verse_number": 139,
    "content":
        "تو انہوں نے ہود کو جھٹلایا تو ہم نے ان کو ہلاک کر ڈالا۔ بےشک اس میں نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 140,
    "content": "اور تمہارا پروردگار تو غالب اور مہربان ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 141,
    "content": "(اور) قوم ثمود نے بھی پیغمبروں کو جھٹلا دیا"
  },
  {
    "surah_number": 26,
    "verse_number": 142,
    "content": "جب ان سے ان کے بھائی صالح نے کہا کہ تم ڈرتے کیوں نہیں؟"
  },
  {
    "surah_number": 26,
    "verse_number": 143,
    "content": "میں تو تمہارا امانت دار ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 144,
    "content": "تو خدا سے ڈرو اور میرا کہا مانو"
  },
  {
    "surah_number": 26,
    "verse_number": 145,
    "content":
        "اور میں اس کا تم سے بدلہ نہیں مانگتا۔ میرا بدلہ (خدا) رب العالمین کے ذمے ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 146,
    "content":
        "کیا وہ چیزیں (تمہیں یہاں میسر) ہیں ان میں تم بےخوف چھوڑ دیئے جاؤ گے"
  },
  {"surah_number": 26, "verse_number": 147, "content": "(یعنی) باغ اور چشمے"},
  {
    "surah_number": 26,
    "verse_number": 148,
    "content": "اور کھیتیاں اور کھجوریں جن کے خوشے لطیف ونازک ہوتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 149,
    "content": "اور تکلف سے پہاڑوں میں تراش خراش کر گھر بناتے ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 150,
    "content": "تو خدا سے ڈرو اور میرے کہنے پر چلو"
  },
  {
    "surah_number": 26,
    "verse_number": 151,
    "content": "اور حد سے تجاوز کرنے والوں کی بات نہ مانو"
  },
  {
    "surah_number": 26,
    "verse_number": 152,
    "content": "جو ملک میں فساد کرتے ہیں اور اصلاح نہیں کرتے"
  },
  {
    "surah_number": 26,
    "verse_number": 153,
    "content": "وہ کہنے لگے کہ تم تو جادو زدہ ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 154,
    "content":
        "تم اور کچھ نہیں ہماری طرح آدمی ہو۔ اگر سچے ہو تو کوئی نشانی پیش کرو"
  },
  {
    "surah_number": 26,
    "verse_number": 155,
    "content":
        "صالح نے کہا (دیکھو) یہ اونٹنی ہے (ایک دن) اس کی پانی پینے کی باری ہے اور ایک معین روز تمہاری باری"
  },
  {
    "surah_number": 26,
    "verse_number": 156,
    "content": "اور اس کو کوئی تکلیف نہ دینا (نہیں تو) تم کو سخت عذاب آ پکڑے گا"
  },
  {
    "surah_number": 26,
    "verse_number": 157,
    "content": "تو انہوں نے اس کی کونچیں کاٹ ڈالیں پھر نادم ہوئے"
  },
  {
    "surah_number": 26,
    "verse_number": 158,
    "content":
        "سو ان کو عذاب نے آن پکڑا۔ بےشک اس میں نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 159,
    "content": "اور تمہارا پروردگار تو غالب (اور) مہربان ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 160,
    "content": "(اور قوم) لوط نے بھی پیغمبروں کو جھٹلایا"
  },
  {
    "surah_number": 26,
    "verse_number": 161,
    "content": "جب ان سے ان کے بھائی لوط نے کہا کہ تم کیوں نہیں ڈرتے؟"
  },
  {
    "surah_number": 26,
    "verse_number": 162,
    "content": "میں تو تمہارا امانت دار پیغمبر ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 163,
    "content": "تو خدا سے ڈرو اور میرا کہا مانو"
  },
  {
    "surah_number": 26,
    "verse_number": 164,
    "content":
        "اور میں تم سے اس (کام) کا بدلہ نہیں مانگتا۔ میرا بدلہ (خدائے) رب العالمین کے ذمے ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 165,
    "content": "کیا تم اہل عالم میں سے لڑکوں پر مائل ہوتے ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 166,
    "content":
        "اور تمہارے پروردگار نے جو تمہارے لئے تمہاری بیویاں پیدا کی ہیں ان کو چھوڑ دیتے ہو۔ حقیقت یہ ہے کہ تم حد سے نکل جانے والے ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 167,
    "content": "وہ کہنے لگے کہ لوط اگر تم باز نہ آؤ گے تو شہر بدر کردیئے جاؤ گے"
  },
  {
    "surah_number": 26,
    "verse_number": 168,
    "content": "لوط نے کہا کہ میں تمہارے کام کا سخت دشمن ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 169,
    "content":
        "اے میرے پروردگار مجھ کو اور میرے گھر والوں کو ان کے کاموں (کے وبال) سے نجات دے"
  },
  {
    "surah_number": 26,
    "verse_number": 170,
    "content": "سو ہم نے ان کو اور ان کے گھر والوں کو سب کو نجات دی"
  },
  {
    "surah_number": 26,
    "verse_number": 171,
    "content": "مگر ایک بڑھیا کہ پیچھے رہ گئی"
  },
  {
    "surah_number": 26,
    "verse_number": 172,
    "content": "پھر ہم نے اوروں کو ہلاک کردیا"
  },
  {
    "surah_number": 26,
    "verse_number": 173,
    "content":
        "اور ان پر مینھہ برسایا۔ سو جو مینھہ ان (لوگوں) پر (برسا) جو ڈرائے گئے برا تھا"
  },
  {
    "surah_number": 26,
    "verse_number": 174,
    "content": "بےشک اس میں نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 175,
    "content": "اور تمہارا پروردگار تو غالب (اور) مہربان ہے۔"
  },
  {
    "surah_number": 26,
    "verse_number": 176,
    "content": "اور بن کے رہنے والوں نے بھی پیغمبروں کو جھٹلایا"
  },
  {
    "surah_number": 26,
    "verse_number": 177,
    "content": "جب ان سے شعیب نے کہا کہ تم ڈرتے کیوں نہیں؟"
  },
  {
    "surah_number": 26,
    "verse_number": 178,
    "content": "میں تو تمہارا امانت دار پیغمبر ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 179,
    "content": "تو خدا سے ڈرو اور میرا کہا مانو"
  },
  {
    "surah_number": 26,
    "verse_number": 180,
    "content":
        "اور میں اس کام کا تم سے کچھ بدلہ نہیں مانگتا میرا بدلہ تو خدائے رب العالمین کے ذمے ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 181,
    "content": "(دیکھو) پیمانہ پورا بھرا کرو اور نقصان نہ کیا کرو"
  },
  {
    "surah_number": 26,
    "verse_number": 182,
    "content": "اور ترازو سیدھی رکھ کر تولا کرو"
  },
  {
    "surah_number": 26,
    "verse_number": 183,
    "content":
        "اور لوگوں کو ان کی چیزیں کم نہ دیا کرو اور ملک میں فساد نہ کرتے پھرو"
  },
  {
    "surah_number": 26,
    "verse_number": 184,
    "content": "اور اس سے ڈرو جس نے تم کو اور پہلی خلقت کو پیدا کیا"
  },
  {
    "surah_number": 26,
    "verse_number": 185,
    "content": "وہ کہنے لگے کہ تم جادو زدہ ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 186,
    "content":
        "اور تم اور کچھ نہیں ہم ہی جیسے آدمی ہو۔ اور ہمارا خیال ہے کہ تم جھوٹے ہو"
  },
  {
    "surah_number": 26,
    "verse_number": 187,
    "content": "اور اگر سچے ہو تو ہم پر آسمان سے ایک ٹکڑا لا کر گراؤ"
  },
  {
    "surah_number": 26,
    "verse_number": 188,
    "content":
        "شعیب نے کہا کہ جو کام تم کرتے ہو میرا پروردگار اس سے خوب واقف ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 189,
    "content":
        "تو ان لوگوں نے ان کو جھٹلایا، پس سائبان کے عذاب نے ان کو آ پکڑا۔ بےشک وہ بڑے (سخت) دن کا عذاب تھا"
  },
  {
    "surah_number": 26,
    "verse_number": 190,
    "content":
        "اس میں یقیناً نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 191,
    "content": "اور تمہارا پروردگار تو غالب (اور) مہربان ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 192,
    "content": "اور یہ قرآن (خدائے) پروردگار عالم کا اُتارا ہوا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 193,
    "content": "اس کو امانت دار فرشتہ لے کر اُترا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 194,
    "content":
        "(یعنی اس نے) تمہارے دل پر (القا) کیا ہے تاکہ (لوگوں کو) نصیحت کرتے رہو"
  },
  {
    "surah_number": 26,
    "verse_number": 195,
    "content": "اور (القا بھی) فصیح عربی زبان میں (کیا ہے)"
  },
  {
    "surah_number": 26,
    "verse_number": 196,
    "content": "اور اس کی خبر پہلے پیغمبروں کی کتابوں میں (لکھی ہوئی) ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 197,
    "content":
        "کیا ان کے لئے یہ سند نہیں ہے کہ علمائے بنی اسرائیل اس (بات) کو جانتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 198,
    "content": "اور اگر ہم اس کو کسی غیر اہل زبان پر اُتارتے"
  },
  {
    "surah_number": 26,
    "verse_number": 199,
    "content": "اور وہ اسے ان (لوگوں کو) پڑھ کر سناتا تو یہ اسے (کبھی) نہ مانتے"
  },
  {
    "surah_number": 26,
    "verse_number": 200,
    "content": "اسی طرح ہم نے انکار کو گنہگاروں کے دلوں میں داخل کردیا"
  },
  {
    "surah_number": 26,
    "verse_number": 201,
    "content": "وہ جب تک درد دینے والا عذاب نہ دیکھ لیں گے، اس کو نہیں مانیں گے"
  },
  {
    "surah_number": 26,
    "verse_number": 202,
    "content": "وہ ان پر ناگہاں آ واقع ہوگا اور انہیں خبر بھی نہ ہوگی"
  },
  {
    "surah_number": 26,
    "verse_number": 203,
    "content": "اس وقت کہیں گے کیا ہمیں ملہت ملے گی؟"
  },
  {
    "surah_number": 26,
    "verse_number": 204,
    "content": "تو کیا یہ ہمارے عذاب کو جلدی طلب کر رہے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 205,
    "content": "بھلا دیکھو تو اگر ہم ان کو برسوں فائدے دیتے رہے"
  },
  {
    "surah_number": 26,
    "verse_number": 206,
    "content": "پھر ان پر وہ (عذاب) آ واقع ہو جس کا تم سے وعدہ کیا جاتا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 207,
    "content": "تو جو فائدے یہ اٹھاتے رہے ان کے کس کام آئیں گے"
  },
  {
    "surah_number": 26,
    "verse_number": 208,
    "content":
        "اور ہم نے کوئی بستی ہلاک نہیں کی مگر اس کے لئے نصیحت کرنے والے (پہلے بھیج دیتے) تھے"
  },
  {
    "surah_number": 26,
    "verse_number": 209,
    "content": "نصیحت کردیں اور ہم ظالم نہیں ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 210,
    "content": "اور اس (قرآن) کو شیطان لے کر نازل نہیں ہوئے"
  },
  {
    "surah_number": 26,
    "verse_number": 211,
    "content": "یہ کام نہ تو ان کو سزاوار ہے اور نہ وہ اس کی طاقت رکھتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 212,
    "content": "وہ (آسمانی باتوں) کے سننے (کے مقامات) سے الگ کر دیئے گئے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 213,
    "content":
        "تو خدا کے سوا کسی اور معبود کو مت پکارنا، ورنہ تم کو عذاب دیا جائے گا"
  },
  {
    "surah_number": 26,
    "verse_number": 214,
    "content": "اور اپنے قریب کے رشتہ داروں کو ڈر سنا دو"
  },
  {
    "surah_number": 26,
    "verse_number": 215,
    "content": "اور جو مومن تمہارے پیرو ہوگئے ہیں ان سے متواضع پیش آؤ"
  },
  {
    "surah_number": 26,
    "verse_number": 216,
    "content":
        "پھر اگر لوگ تمہاری نافرمانی کریں تو کہہ دو کہ میں تمہارے اعمال سے بےتعلق ہوں"
  },
  {
    "surah_number": 26,
    "verse_number": 217,
    "content": "اور (خدائے) غالب اور مہربان پر بھروسا رکھو"
  },
  {
    "surah_number": 26,
    "verse_number": 218,
    "content": "جو تم کو جب تم (تہجد) کے وقت اُٹھتے ہو دیکھتا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 219,
    "content": "اور نمازیوں میں تمہارے پھرنے کو بھی"
  },
  {
    "surah_number": 26,
    "verse_number": 220,
    "content": "بےشک وہ سننے اور جاننے والا ہے"
  },
  {
    "surah_number": 26,
    "verse_number": 221,
    "content": "(اچھا) میں تمیں بتاؤں کہ شیطان کس پر اُترتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 222,
    "content": "ہر جھوٹے گنہگار پر اُترتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 223,
    "content":
        "جو سنی ہوئی بات (اس کے کام میں) لا ڈالتے ہیں اور وہ اکثر جھوٹے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 224,
    "content": "اور شاعروں کی پیروی گمراہ لوگ کیا کرتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 225,
    "content": "کیا تم نے نہیں دیکھا کہ وہ ہر وادی میں سر مارتے پھرتے ہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 226,
    "content": "اور کہتے وہ ہیں جو کرتے نہیں"
  },
  {
    "surah_number": 26,
    "verse_number": 227,
    "content":
        "مگر جو لوگ ایمان لائے اور نیک کام کئے اور خدا کو بہت یاد کرتے رہے اور اپنے اوپر ظلم ہونے کے بعد انتقام لیا اور ظالم عنقریب جان لیں گے کہ کون سی جگہ لوٹ کر جاتے ہیں"
  },
  {
    "surah_number": 27,
    "verse_number": 1,
    "content": "ٰطٰسٓ۔ یہ قرآن اور روشن کتاب کی آیتیں ہیں"
  },
  {
    "surah_number": 27,
    "verse_number": 2,
    "content": "مومنوں کے لئے ہدایت اور بشارت"
  },
  {
    "surah_number": 27,
    "verse_number": 3,
    "content": "وہ جو نماز پڑھتے اور زکوٰة دیتے اور آخرت کا یقین رکھتے ہیں"
  },
  {
    "surah_number": 27,
    "verse_number": 4,
    "content":
        "جو لوگ آخرت پر ایمان نہیں رکھتے ہیں ہم نے ان کے اعمال ان کے لئے آرستہ کردیئے ہیں تو وہ سرگرداں ہو رہے ہیں"
  },
  {
    "surah_number": 27,
    "verse_number": 5,
    "content":
        "یہی لوگ ہیں جن کے لئے بڑا عذاب ہے اور وہ آخرت میں بھی وہ بہت نقصان اٹھانے والے ہیں"
  },
  {
    "surah_number": 27,
    "verse_number": 6,
    "content": "اور تم کو قرآن (خدائے) حکیم وعلیم کی طرف سے عطا کیا جاتا ہے"
  },
  {
    "surah_number": 27,
    "verse_number": 7,
    "content":
        "جب موسیٰ نے اپنے گھر والوں سے کہا کہ میں نے آگ دیکھی ہے، میں وہاں سے (رستے) کا پتہ لاتا ہوں یا سلگتا ہوا انگارہ تمہارے پاس لاتا ہوں تاکہ تم تاپو"
  },
  {
    "surah_number": 27,
    "verse_number": 8,
    "content":
        "جب موسیٰ اس کے پاس آئے تو ندا آئی کہ وہ جو آگ میں (تجلّی دکھاتا) ہے بابرکت ہے۔ اور جو آگ کے اردگرد ہیں اور خدا جو تمام عالم کا پروردگار ہے پاک ہے"
  },
  {
    "surah_number": 27,
    "verse_number": 9,
    "content": "اے موسیٰ میں ہی خدائے غالب ودانا ہوں"
  },
  {
    "surah_number": 27,
    "verse_number": 10,
    "content":
        "اور اپنی لاٹھی ڈال دو۔ جب اُسے دیکھا تو (اس طرح) ہل رہی تھی گویا سانپ ہے تو پیٹھ پھیر کر بھاگے اور پیچھے مڑ کر نہ دیکھا (حکم ہوا کہ) موسیٰ ڈرو مت۔ ہمارے پاس پیغمبر ڈرا نہیں کرتے"
  },
  {
    "surah_number": 27,
    "verse_number": 11,
    "content":
        "ہاں جس نے ظلم کیا پھر برائی کے بعد اسے نیکی سے بدل دیا تو میں بخشنے والا مہربان ہوں"
  },
  {
    "surah_number": 27,
    "verse_number": 12,
    "content":
        "اور اپنا ہاتھ اپنے گریبان میں ڈالو سفید نکلے گا۔ (ان دو معجزوں کے ساتھ جو) نو معجزوں میں (داخل ہیں) فرعون اور اس کی قوم کے پاس جاؤ کہ وہ بےحکم لوگ ہیں"
  },
  {
    "surah_number": 27,
    "verse_number": 13,
    "content":
        "جب ان کے پاس ہماری روشن نشانیاں پہنچیں، کہنے لگے یہ صریح جادو ہے"
  },
  {
    "surah_number": 27,
    "verse_number": 14,
    "content":
        "اور بےانصافی اور غرور سے ان سے انکار کیا لیکن ان کے دل ان کو مان چکے تھے۔ سو دیکھ لو فساد کرنے والوں کا انجام کیسا ہوا"
  },
  {
    "surah_number": 27,
    "verse_number": 15,
    "content":
        "اور ہم نے داؤد اور سلیمان کو علم بخشا اور انہوں نے کہا کہ خدا کا شکر ہے جس نے ہمیں بہت سے مومن بندوں پر فضلیت دی"
  },
  {
    "surah_number": 27,
    "verse_number": 16,
    "content":
        "اور سلیمان اور داؤد کے قائم مقام ہوئے۔ اور کہنے لگے کہ لوگو! ہمیں (خدا کی طرف سے) جانوروں کی بولی سکھائی گئی ہے اور ہر چیز عنایت فرمائی گئی ہے۔ بےشک یہ (اُس کا) صریح فضل ہے"
  },
  {
    "surah_number": 27,
    "verse_number": 17,
    "content":
        "اور سلیمان کے لئے جنوں اور انسانوں اور پرندوں کے لشکر جمع کئے گئے اور قسم وار کئے جاتے تھے"
  },
  {
    "surah_number": 27,
    "verse_number": 18,
    "content":
        "یہاں تک کہ جب چیونٹیوں کے میدان میں پہنچے تو ایک چیونٹی نے کہا کہ چیونٹیوں اپنے اپنے بلوں میں داخل ہو جاؤ ایسا نہ ہو کہ سلیمان اور اس کے لشکر تم کو کچل ڈالیں اور ان کو خبر بھی نہ ہو"
  },
  {
    "surah_number": 27,
    "verse_number": 19,
    "content":
        "تو وہ اس کی بات سن کر ہنس پڑے اور کہنے لگے کہ اے پروردگار! مجھے توفیق عطا فرما کہ جو احسان تونے مجھ پر اور میرے ماں باپ پر کئے ہیں ان کا شکر کروں اور ایسے نیک کام کروں کہ تو ان سے خوش ہوجائے اور مجھے اپنی رحمت سے اپنے نیک بندوں میں داخل فرما"
  },
  {
    "surah_number": 27,
    "verse_number": 20,
    "content":
        "انہوں نے جانوروں کا جائزہ لیا تو کہنے لگے کیا سبب ہے کہ ہُدہُد نظر نہیں آتا۔ کیا کہیں غائب ہوگیا ہے؟"
  },
  {
    "surah_number": 27,
    "verse_number": 21,
    "content":
        "میں اسے سخت سزا دوں گا یا ذبح کر ڈالوں گا یا میرے سامنے (اپنی بےقصوری کی) دلیل صریح پیش کرے"
  },
  {
    "surah_number": 27,
    "verse_number": 22,
    "content":
