  {
    "surah_number": 53,
    "verse_number": 34,
    "content": "اور تھوڑا سا دیا (پھر) ہاتھ روک لیا"
  },
  {
    "surah_number": 53,
    "verse_number": 35,
    "content": "کیا اس کے پاس غیب کا علم ہے کہ وہ اس کو دیکھ رہا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 36,
    "content": "کیا جو باتیں موسیٰ کے صحیفوں میں ہیں ان کی اس کو خبر نہیں پہنچی"
  },
  {
    "surah_number": 53,
    "verse_number": 37,
    "content": "اور ابراہیمؑ کی جنہوں نے (حق طاعت ورسالت) پورا کیا"
  },
  {
    "surah_number": 53,
    "verse_number": 38,
    "content": "یہ کہ کوئی شخص دوسرے (کے گناہ) کا بوجھ نہیں اٹھائے گا"
  },
  {
    "surah_number": 53,
    "verse_number": 39,
    "content": "اور یہ کہ انسان کو وہی ملتا ہے جس کی وہ کوشش کرتا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 40,
    "content": "اور یہ کہ اس کی کوشش دیکھی جائے گی"
  },
  {
    "surah_number": 53,
    "verse_number": 41,
    "content": "پھر اس کو اس کا پورا پورا بدلا دیا جائے گا"
  },
  {
    "surah_number": 53,
    "verse_number": 42,
    "content": "اور یہ کہ تمہارے پروردگار ہی کے پاس پہنچنا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 43,
    "content": "اور یہ کہ وہ ہنساتا اور رلاتا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 44,
    "content": "اور یہ کہ وہی مارتا اور جلاتا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 45,
    "content": "اور یہ کہ وہی نر اور مادہ دو قسم (کے حیوان) پیدا کرتا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 46,
    "content": "(یعنی) نطفے سے جو (رحم میں) ڈالا جاتا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 47,
    "content": "اور یہ کہ (قیامت کو) اسی پر دوبارہ اٹھانا لازم ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 48,
    "content": "اور یہ کہ وہی دولت مند بناتا اور مفلس کرتا ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 49,
    "content": "اور یہ کہ وہی شعریٰ کا مالک ہے"
  },
  {
    "surah_number": 53,
    "verse_number": 50,
    "content": "اور یہ کہ اسی نے عاد اول کو ہلاک کر ڈالا"
  },
  {
    "surah_number": 53,
    "verse_number": 51,
    "content": "اور ثمود کو بھی۔ غرض کسی کو باقی نہ چھوڑا"
  },
  {
    "surah_number": 53,
    "verse_number": 52,
    "content":
        "اور ان سے پہلے قوم نوحؑ کو بھی۔ کچھ شک نہیں کہ وہ لوگ بڑے ہی ظالم اور بڑے ہی سرکش تھے"
  },
  {
    "surah_number": 53,
    "verse_number": 53,
    "content": "اور اسی نے الٹی ہوئی بستیوں کو دے پٹکا"
  },
  {
    "surah_number": 53,
    "verse_number": 54,
    "content": "پھر ان پر چھایا جو چھایا"
  },
  {
    "surah_number": 53,
    "verse_number": 55,
    "content": "تو (اے انسان) تو اپنے پروردگار کی کون سی نعمت پر جھگڑے گا"
  },
  {
    "surah_number": 53,
    "verse_number": 56,
    "content": "یہ (محمدﷺ) بھی اگلے ڈر سنانے والوں میں سے ایک ڈر سنانے والے ہیں"
  },
  {
    "surah_number": 53,
    "verse_number": 57,
    "content": "آنے والی (یعنی قیامت) قریب آ پہنچی"
  },
  {
    "surah_number": 53,
    "verse_number": 58,
    "content": "اس (دن کی تکلیفوں) کو خدا کے سوا کوئی دور نہیں کرسکے گا"
  },
  {
    "surah_number": 53,
    "verse_number": 59,
    "content": "اے منکرین خدا) کیا تم اس کلام سے تعجب کرتے ہو؟"
  },
  {
    "surah_number": 53,
    "verse_number": 60,
    "content": "اور ہنستے ہو اور روتے نہیں؟"
  },
  {
    "surah_number": 53,
    "verse_number": 61,
    "content": "اور تم غفلت میں پڑ رہے ہو"
  },
  {
    "surah_number": 53,
    "verse_number": 62,
    "content": "تو خدا کے آگے سجدہ کرو اور (اسی کی) عبادت کرو"
  },
  {
    "surah_number": 54,
    "verse_number": 1,
    "content": "قیامت قریب آ پہنچی اور چاند شق ہوگیا"
  },
  {
    "surah_number": 54,
    "verse_number": 2,
    "content":
        "اور اگر (کافر) کوئی نشانی دیکھتے ہیں تو منہ پھیر لیتے ہیں اور کہتے ہیں کہ یہ ایک ہمیشہ کا جادو ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 3,
    "content":
        "اور انہوں نے جھٹلایا اور اپنی خواہشوں کی پیروی کی اور ہر کام کا وقت مقرر ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 4,
    "content": "اور ان کو ایسے حالات (سابقین) پہنچ چکے ہیں جن میں عبرت ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 5,
    "content":
        "اور کامل دانائی (کی کتاب بھی) لیکن ڈرانا ان کو کچھ فائدہ نہیں دیتا"
  },
  {
    "surah_number": 54,
    "verse_number": 6,
    "content":
        "تو تم بھی ان کی کچھ پروا نہ کرو۔ جس دن بلانے والا ان کو ایک ناخوش چیز کی طرف بلائے گا"
  },
  {
    "surah_number": 54,
    "verse_number": 7,
    "content":
        "تو آنکھیں نیچی کئے ہوئے قبروں سے نکل پڑیں گے گویا بکھری ہوئی ٹڈیاں"
  },
  {
    "surah_number": 54,
    "verse_number": 8,
    "content":
        "اس بلانے والے کی طرف دوڑتے جاتے ہوں گے۔ کافر کہیں گے یہ دن بڑا سخت ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 9,
    "content":
        "ان سے پہلے نوحؑ کی قوم نے بھی تکذیب کی تھی تو انہوں نے ہمارے بندے کو جھٹلایا اور کہا کہ دیوانہ ہے اور انہیں ڈانٹا بھی"
  },
  {
    "surah_number": 54,
    "verse_number": 10,
    "content":
        "تو انہوں نے اپنے پروردگار سے دعا کی کہ (بار الٓہا) میں (ان کے مقابلے میں) کمزور ہوں تو (ان سے) بدلہ لے"
  },
  {
    "surah_number": 54,
    "verse_number": 11,
    "content": "پس ہم نے زور کے مینہ سے آسمان کے دہانے کھول دیئے"
  },
  {
    "surah_number": 54,
    "verse_number": 12,
    "content":
        "اور زمین میں چشمے جاری کردیئے تو پانی ایک کام کے لئے جو مقدر ہوچکا تھا جمع ہوگیا"
  },
  {
    "surah_number": 54,
    "verse_number": 13,
    "content":
        "اور ہم نے نوحؑ کو ایک کشتی پر جو تختوں اور میخوں سے تیار کی گئی تھی سوار کرلیا"
  },
  {
    "surah_number": 54,
    "verse_number": 14,
    "content":
        "وہ ہماری آنکھوں کے سامنے چلتی تھی۔ (یہ سب کچھ) اس شخص کے انتقام کے لئے (کیا گیا) جس کو کافر مانتے نہ تھے"
  },
  {
    "surah_number": 54,
    "verse_number": 15,
    "content": "اور ہم نے اس کو ایک عبرت بنا چھوڑا تو کوئی ہے کہ سوچے سمجھے؟"
  },
  {
    "surah_number": 54,
    "verse_number": 16,
    "content": "سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا؟"
  },
  {
    "surah_number": 54,
    "verse_number": 17,
    "content":
        "اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟"
  },
  {
    "surah_number": 54,
    "verse_number": 18,
    "content":
        "عاد نے بھی تکذیب کی تھی سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا"
  },
  {
    "surah_number": 54,
    "verse_number": 19,
    "content": "ہم نے ان پر سخت منحوس دن میں آندھی چلائی"
  },
  {
    "surah_number": 54,
    "verse_number": 20,
    "content":
        "وہ لوگوں کو (اس طرح) اکھیڑے ڈالتی تھی گویا اکھڑی ہوئی کھجوروں کے تنے ہیں"
  },
  {
    "surah_number": 54,
    "verse_number": 21,
    "content": "سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا"
  },
  {
    "surah_number": 54,
    "verse_number": 22,
    "content":
        "اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟"
  },
  {
    "surah_number": 54,
    "verse_number": 23,
    "content": "ثمود نے بھی ہدایت کرنے والوں کو جھٹلایا"
  },
  {
    "surah_number": 54,
    "verse_number": 24,
    "content":
        "اور کہا کہ بھلا ایک آدمی جو ہم ہی میں سے ہے ہم اس کی پیروی کریں؟ یوں ہو تو ہم گمراہی اور دیوانگی میں پڑ گئے"
  },
  {
    "surah_number": 54,
    "verse_number": 25,
    "content":
        "کیا ہم سب میں سے اسی پر وحی نازل ہوئی ہے؟ (نہیں) بلکہ یہ جھوٹا خود پسند ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 26,
    "content": "ان کو کل ہی معلوم ہوجائے گا کہ کون جھوٹا خود پسند ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 27,
    "content":
        "(اے صالح) ہم ان کی آزمائش کے لئے اونٹنی بھیجنے والے ہیں تو تم ان کو دیکھتے رہو اور صبر کرو"
  },
  {
    "surah_number": 54,
    "verse_number": 28,
    "content":
        "اور ان کو آگاہ کردو کہ ان میں پانی کی باری مقرر کر دی گئی ہے۔ ہر (باری والے کو اپنی) باری پر آنا چاہیئے"
  },
  {
    "surah_number": 54,
    "verse_number": 29,
    "content":
        "تو ان لوگوں نے اپنے رفیق کو بلایا اور اس نے (اونٹنی کو پکڑ کر اس کی) کونچیں کاٹ ڈالیں"
  },
  {
    "surah_number": 54,
    "verse_number": 30,
    "content": "سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا"
  },
  {
    "surah_number": 54,
    "verse_number": 31,
    "content":
        "ہم نے ان پر (عذاب کے لئے) ایک چیخ بھیجی تو وہ ایسے ہوگئے جیسے باڑ والے کی سوکھی اور ٹوٹی ہوئی باڑ"
  },
  {
    "surah_number": 54,
    "verse_number": 32,
    "content":
        "اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟"
  },
  {
    "surah_number": 54,
    "verse_number": 33,
    "content": "لوط کی قوم نے بھی ڈر سنانے والوں کو جھٹلایا تھا"
  },
  {
    "surah_number": 54,
    "verse_number": 34,
    "content":
        "تو ہم نے ان پر کنکر بھری ہوا چلائی مگر لوط کے گھر والے کہ ہم نے ان کو پچھلی رات ہی سے بچا لیا"
  },
  {
    "surah_number": 54,
    "verse_number": 35,
    "content": "اپنے فضل سے۔ شکر کرنے والوں کو ہم ایسا ہی بدلہ دیا کرتے ہیں"
  },
  {
    "surah_number": 54,
    "verse_number": 36,
    "content":
        "اور لوطؑ نے ان کو ہماری پکڑ سے ڈرایا بھی تھا مگر انہوں نے ڈرانے میں شک کیا"
  },
  {
    "surah_number": 54,
    "verse_number": 37,
    "content":
        "اور ان سے ان کے مہمانوں کو لے لینا چاہا تو ہم نے ان کی آنکھیں مٹا دیں سو (اب) میرے عذاب اور ڈرانے کے مزے چکھو"
  },
  {
    "surah_number": 54,
    "verse_number": 38,
    "content": "اور ان پر صبح سویرے ہی اٹل عذاب آ نازل ہوا"
  },
  {
    "surah_number": 54,
    "verse_number": 39,
    "content": "تو اب میرے عذاب اور ڈرانے کے مزے چکھو"
  },
  {
    "surah_number": 54,
    "verse_number": 40,
    "content":
        "اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟"
  },
  {
    "surah_number": 54,
    "verse_number": 41,
    "content": "اور قوم فرعون کے پاس بھی ڈر سنانے والے آئے"
  },
  {
    "surah_number": 54,
    "verse_number": 42,
    "content":
        "انہوں نے ہماری تمام نشانیوں کو جھٹلایا تو ہم نے ان کو اس طرح پکڑ لیا جس طرح ایک قوی اور غالب شخص پکڑ لیتا ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 43,
    "content":
        "(اے اہل عرب) کیا تمہارے کافر ان لوگوں سے بہتر ہیں یا تمہارے لئے (پہلی) کتابوں میں کوئی فارغ خطی لکھ دی گئی ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 44,
    "content": "کیا یہ لوگ کہتے ہیں کہ ہماری جماعت بڑی مضبوط ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 45,
    "content":
        "عنقریب یہ جماعت شکست کھائے گی اور یہ لوگ پیٹھ پھیر کر بھاگ جائیں گے"
  },
  {
    "surah_number": 54,
    "verse_number": 46,
    "content": "ان کے وعدے کا وقت تو قیامت ہے اور قیامت بڑی سخت اور بہت تلخ ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 47,
    "content": "بےشک گنہگار لوگ گمراہی اور دیوانگی میں (مبتلا) ہیں"
  },
  {
    "surah_number": 54,
    "verse_number": 48,
    "content": "اس روز منہ کے بل دوزخ میں گھسیٹے جائیں گے اب آگ کا مزہ چکھو"
  },
  {
    "surah_number": 54,
    "verse_number": 49,
    "content": "ہم نے ہر چیز اندازہٴ مقرر کے ساتھ پیدا کی ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 50,
    "content": "اور ہمارا حکم تو آنکھ کے جھپکنے کی طرح ایک بات ہوتی ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 51,
    "content":
        "اور ہم تمہارے ہم مذہبوں کو ہلاک کرچکے ہیں تو کوئی ہے کہ سوچے سمجھے؟"
  },
  {
    "surah_number": 54,
    "verse_number": 52,
    "content": "اور جو کچھ انہوں نے کیا، (ان کے) اعمال ناموں میں (مندرج) ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 53,
    "content": "(یعنی) ہر چھوٹا اور بڑا کام لکھ دیا گیا ہے"
  },
  {
    "surah_number": 54,
    "verse_number": 54,
    "content": "جو پرہیزگار ہیں وہ باغوں اور نہروں میں ہوں گے"
  },
  {
    "surah_number": 54,
    "verse_number": 55,
    "content":
        "(یعنی) پاک مقام میں ہر طرح کی قدرت رکھنے والے بادشاہ کی بارگاہ میں"
  },
  {"surah_number": 55, "verse_number": 1, "content": "(خدا جو) نہایت مہربان"},
  {
    "surah_number": 55,
    "verse_number": 2,
    "content": "اسی نے قرآن کی تعلیم فرمائی"
  },
  {
    "surah_number": 55,
    "verse_number": 3,
    "content": "اسی نے انسان کو پیدا کیا"
  },
  {
    "surah_number": 55,
    "verse_number": 4,
    "content": "اسی نے اس کو بولنا سکھایا"
  },
  {
    "surah_number": 55,
    "verse_number": 5,
    "content": "سورج اور چاند ایک حساب مقرر سے چل رہے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 6,
    "content": "اور بوٹیاں اور درخت سجدہ کر رہے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 7,
    "content": "اور اسی نے آسمان کو بلند کیا اور ترازو قائم کی"
  },
  {
    "surah_number": 55,
    "verse_number": 8,
    "content": "کہ ترازو (سے تولنے) میں حد سے تجاوز نہ کرو"
  },
  {
    "surah_number": 55,
    "verse_number": 9,
    "content": "اور انصاف کے ساتھ ٹھیک تولو۔ اور تول کم مت کرو"
  },
  {
    "surah_number": 55,
    "verse_number": 10,
    "content": "اور اسی نے خلقت کے لئے زمین بچھائی"
  },
  {
    "surah_number": 55,
    "verse_number": 11,
    "content": "اس میں میوے اور کھجور کے درخت ہیں جن کے خوشوں پر غلاف ہوتے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 12,
    "content": "اور اناج جس کے ساتھ بھس ہوتا ہے اور خوشبودار پھول"
  },
  {
    "surah_number": 55,
    "verse_number": 13,
    "content":
        "تو (اے گروہ جن وانس) تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 14,
    "content": "اسی نے انسان کو ٹھیکرے کی طرح کھنکھناتی مٹی سے بنایا"
  },
  {
    "surah_number": 55,
    "verse_number": 15,
    "content": "اور جنات کو آگ کے شعلے سے پیدا کیا"
  },
  {
    "surah_number": 55,
    "verse_number": 16,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 17,
    "content": "وہی دونوں مشرقوں اور دونوں مغربوں کا مالک (ہے)"
  },
  {
    "surah_number": 55,
    "verse_number": 18,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 19,
    "content": "اسی نے دو دریا رواں کئے جو آپس میں ملتے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 20,
    "content": "دونوں میں ایک آڑ ہے کہ (اس سے) تجاوز نہیں کرسکتے"
  },
  {
    "surah_number": 55,
    "verse_number": 21,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 22,
    "content": "دونوں دریاؤں سے موتی اور مونگے نکلتے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 23,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 24,
    "content":
        "اور جہاز بھی اسی کے ہیں جو دریا میں پہاڑوں کی طرح اونچے کھڑے ہوتے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 25,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 26,
    "content": "جو (مخلوق) زمین پر ہے سب کو فنا ہونا ہے"
  },
  {
    "surah_number": 55,
    "verse_number": 27,
    "content":
        "اور تمہارے پروردگار ہی کی ذات (بابرکات) جو صاحب جلال وعظمت ہے باقی رہے گی"
  },
  {
    "surah_number": 55,
    "verse_number": 28,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 29,
    "content":
        "آسمان اور زمین میں جتنے لوگ ہیں سب اسی سے مانگتے ہیں۔ وہ ہر روز کام میں مصروف رہتا ہے"
  },
  {
    "surah_number": 55,
    "verse_number": 30,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 31,
    "content": "اے دونوں جماعتو! ہم عنقریب تمہاری طرف متوجہ ہوتے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 32,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 33,
    "content":
        "اے گروہِ جن وانس اگر تمہیں قدرت ہو کہ آسمان اور زمین کے کناروں سے نکل جاؤ تو نکل جاؤ۔ اور زور کے سوا تم نکل سکنے ہی کے نہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 34,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 35,
    "content":
        "تم پر آگ کے شعلے اور دھواں چھوڑ دیا جائے گا تو پھر تم مقابلہ نہ کرسکو گے"
  },
  {
    "surah_number": 55,
    "verse_number": 36,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 37,
    "content":
        "پھر جب آسمان پھٹ کر تیل کی تلچھٹ کی طرح گلابی ہوجائے گا (تو) وہ کیسا ہولناک دن ہوگا"
  },
  {
    "surah_number": 55,
    "verse_number": 38,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 39,
    "content":
        "اس روز نہ تو کسی انسان سے اس کے گناہوں کے بارے میں پرسش کی جائے گی اور نہ کسی جن سے"
  },
  {
    "surah_number": 55,
    "verse_number": 40,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 41,
    "content":
        "گنہگار اپنے چہرے ہی سے پہچان لئے جائیں گے تو پیشانی کے بالوں اور پاؤں سے پکڑ لئے جائیں گے"
  },
  {
    "surah_number": 55,
    "verse_number": 42,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 43,
    "content": "یہی وہ جہنم ہے جسے گنہگار لوگ جھٹلاتے تھے"
  },
  {
    "surah_number": 55,
    "verse_number": 44,
    "content": "وہ دوزخ اور کھولتے ہوئے گرم پانی کے درمیان گھومتے پھریں گے"
  },
  {
    "surah_number": 55,
    "verse_number": 45,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 46,
    "content":
        "اور جو شخص اپنے پروردگار کے سامنے کھڑے ہونے سے ڈرا اس کے لئے دو باغ ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 47,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 48,
    "content": "ان دونوں میں بہت سی شاخیں (یعنی قسم قسم کے میووں کے درخت ہیں)"
  },
  {
    "surah_number": 55,
    "verse_number": 49,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 50,
    "content": "ان میں دو چشمے بہہ رہے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 51,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 52,
    "content": "ان میں سب میوے دو دو قسم کے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 53,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 54,
    "content":
        "(اہل جنت) ایسے بچھونوں پر جن کے استرا طلس کے ہیں تکیہ لگائے ہوئے ہوں گے۔ اور دونوں باغوں کے میوے قریب (جھک رہے) ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 55,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 56,
    "content":
        "ان میں نیچی نگاہ والی عورتیں ہیں جن کو اہل جنت سے پہلے نہ کسی انسان نے ہاتھ لگایا اور نہ کسی جن نے"
  },
  {
    "surah_number": 55,
    "verse_number": 57,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 58,
    "content": "گویا وہ یاقوت اور مرجان ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 59,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 60,
    "content": "نیکی کا بدلہ نیکی کے سوا کچھ نہیں ہے"
  },
  {
    "surah_number": 55,
    "verse_number": 61,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 62,
    "content": "اور ان باغوں کے علاوہ دو باغ اور ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 63,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {"surah_number": 55, "verse_number": 64, "content": "دونوں خوب گہرے سبز"},
  {
    "surah_number": 55,
    "verse_number": 65,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 66,
    "content": "ان میں دو چشمے ابل رہے ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 67,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 68,
    "content": "ان میں میوے اور کھجوریں اور انار ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 69,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 70,
    "content": "ان میں نیک سیرت (اور) خوبصورت عورتیں ہیں"
  },
  {
    "surah_number": 55,
    "verse_number": 71,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 72,
    "content": "(وہ) حوریں (ہیں جو) خیموں میں مستور (ہیں)"
  },
  {
    "surah_number": 55,
    "verse_number": 73,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 74,
    "content":
        "ان کو اہل جنت سے پہلے نہ کسی انسان نے ہاتھ لگایا اور نہ کسی جن نے"
  },
  {
    "surah_number": 55,
    "verse_number": 75,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 76,
    "content": "سبز قالینوں اور نفیس مسندوں پر تکیہ لگائے بیٹھے ہوں گے"
  },
  {
    "surah_number": 55,
    "verse_number": 77,
    "content": "تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟"
  },
  {
    "surah_number": 55,
    "verse_number": 78,
    "content":
        "(اے محمدﷺ) تمہارا پروردگار جو صاحب جلال وعظمت ہے اس کا نام بڑا بابرکت ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 1,
    "content": "جب واقع ہونے والی واقع ہوجائے"
  },
  {
    "surah_number": 56,
    "verse_number": 2,
    "content": "اس کے واقع ہونے میں کچھ جھوٹ نہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 3,
    "content": "کسی کو پست کرے کسی کو بلند"
  },
  {
    "surah_number": 56,
    "verse_number": 4,
    "content": "جب زمین بھونچال سے لرزنے لگے"
  },
  {
    "surah_number": 56,
    "verse_number": 5,
    "content": "اور پہاڑ ٹوٹ کر ریزہ ریزہ ہوجائیں"
  },
  {
    "surah_number": 56,
    "verse_number": 6,
    "content": "پھر غبار ہو کر اُڑنے لگیں"
  },
  {
    "surah_number": 56,
    "verse_number": 7,
    "content": "اور تم لوگ تین قسم ہوجاؤ"
  },
  {
    "surah_number": 56,
    "verse_number": 8,
    "content":
        "تو داہنے ہاتھ والے (سبحان الله) داہنے ہاتھ والے کیا (ہی چین میں) ہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 9,
    "content":
        "اور بائیں ہاتھ والے (افسوس) بائیں ہاتھ والے کیا (گرفتار عذاب) ہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 10,
    "content":
        "اور جو آگے بڑھنے والے ہیں (ان کا کیا کہنا) وہ آگے ہی بڑھنے والے ہیں"
  },
  {"surah_number": 56, "verse_number": 11, "content": "وہی (خدا کے) مقرب ہیں"},
  {"surah_number": 56, "verse_number": 12, "content": "نعمت کے بہشتوں میں"},
  {
    "surah_number": 56,
    "verse_number": 13,
    "content": "وہ بہت سے تو اگلے لوگوں میں سے ہوں گے"
  },
  {
    "surah_number": 56,
    "verse_number": 14,
    "content": "اور تھوڑے سے پچھلوں میں سے"
  },
  {
    "surah_number": 56,
    "verse_number": 15,
    "content": "(لعل و یاقوت وغیرہ سے) جڑے ہوئے تختوں پر"
  },
  {
    "surah_number": 56,
    "verse_number": 16,
    "content": "آمنے سامنے تکیہ لگائے ہوئے"
  },
  {
    "surah_number": 56,
    "verse_number": 17,
    "content":
        "نوجوان خدمت گزار جو ہمیشہ (ایک ہی حالت میں) رہیں گے ان کے آس پاس پھریں گے"
  },
  {
    "surah_number": 56,
    "verse_number": 18,
    "content": "یعنی آبخورے اور آفتابے اور صاف شراب کے گلاس لے لے کر"
  },
  {
    "surah_number": 56,
    "verse_number": 19,
    "content": "اس سے نہ تو سر میں درد ہوگا اور نہ ان کی عقلیں زائل ہوں گی"
  },
  {
    "surah_number": 56,
    "verse_number": 20,
    "content": "اور میوے جس طرح کے ان کو پسند ہوں"
  },
  {
    "surah_number": 56,
    "verse_number": 21,
    "content": "اور پرندوں کا گوشت جس قسم کا ان کا جی چاہے"
  },
  {
    "surah_number": 56,
    "verse_number": 22,
    "content": "اور بڑی بڑی آنکھوں والی حوریں"
  },
  {
    "surah_number": 56,
    "verse_number": 23,
    "content": "جیسے (حفاظت سے) تہہ کئے ہوئے (آب دار) موتی"
  },
  {
    "surah_number": 56,
    "verse_number": 24,
    "content": "یہ ان اعمال کا بدلہ ہے جو وہ کرتے تھے"
  },
  {
    "surah_number": 56,
    "verse_number": 25,
    "content": "وہاں نہ بیہودہ بات سنیں گے اور نہ گالی گلوچ"
  },
  {
    "surah_number": 56,
    "verse_number": 26,
    "content": "ہاں ان کا کلام سلام سلام (ہوگا)"
  },
  {
    "surah_number": 56,
    "verse_number": 27,
    "content":
        "اور داہنے ہاتھ والے (سبحان الله) داہنے ہاتھ والے کیا (ہی عیش میں) ہیں"
  },
  {"surah_number": 56, "verse_number": 28, "content": "(یعنی) بےخار کی بیریوں"},
  {"surah_number": 56, "verse_number": 29, "content": "اور تہہ بہ تہہ کیلوں"},
  {"surah_number": 56, "verse_number": 30, "content": "اور لمبے لمبے سایوں"},
  {"surah_number": 56, "verse_number": 31, "content": "اور پانی کے جھرنوں"},
  {
    "surah_number": 56,
    "verse_number": 32,
    "content": "اور میوہ ہائے کثیرہ (کے باغوں) میں"
  },
  {
    "surah_number": 56,
    "verse_number": 33,
    "content": "جو نہ کبھی ختم ہوں اور نہ ان سے کوئی روکے"
  },
  {
    "surah_number": 56,
    "verse_number": 34,
    "content": "اور اونچے اونچے فرشوں میں"
  },
  {
    "surah_number": 56,
    "verse_number": 35,
    "content": "ہم نے ان (حوروں) کو پیدا کیا"
  },
  {
    "surah_number": 56,
    "verse_number": 36,
    "content": "تو ان کو کنواریاں بنایا"
  },
  {
    "surah_number": 56,
    "verse_number": 37,
    "content": "(اور شوہروں کی) پیاریاں اور ہم عمر"
  },
  {
    "surah_number": 56,
    "verse_number": 38,
    "content": "یعنی داہنے ہاتھ والوں کے لئے"
  },
  {
    "surah_number": 56,
    "verse_number": 39,
    "content": "(یہ) بہت سے اگلے لوگوں میں سے ہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 40,
    "content": "اور بہت سے پچھلوں میں سے"
  },
  {
    "surah_number": 56,
    "verse_number": 41,
    "content":
        "اور بائیں ہاتھ والے (افسوس) بائیں ہاتھ والے کیا (ہی عذاب میں) ہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 42,
    "content": "(یعنی دوزخ کی) لپٹ اور کھولتے ہوئے پانی میں"
  },
  {
    "surah_number": 56,
    "verse_number": 43,
    "content": "اور سیاہ دھوئیں کے سائے میں"
  },
  {
    "surah_number": 56,
    "verse_number": 44,
    "content": "(جو) نہ ٹھنڈا (ہے) نہ خوشنما"
  },
  {
    "surah_number": 56,
    "verse_number": 45,
    "content": "یہ لوگ اس سے پہلے عیشِ نعیم میں پڑے ہوئے تھے"
  },
  {
    "surah_number": 56,
    "verse_number": 46,
    "content": "اور گناہ عظیم پر اڑے ہوئے تھے"
  },
  {
    "surah_number": 56,
    "verse_number": 47,
    "content":
        "اور کہا کرتے تھے کہ بھلا جب ہم مرگئے اور مٹی ہوگئے اور ہڈیاں (ہی ہڈیاں رہ گئے) تو کیا ہمیں پھر اُٹھنا ہوگا؟"
  },
  {
    "surah_number": 56,
    "verse_number": 48,
    "content": "اور کیا ہمارے باپ دادا کو بھی؟"
  },
  {
    "surah_number": 56,
    "verse_number": 49,
    "content": "کہہ دو کہ بےشک پہلے اور پچھلے"
  },
  {
    "surah_number": 56,
    "verse_number": 50,
    "content": "(سب) ایک روز مقرر کے وقت پر جمع کئے جائیں گے"
  },
  {
    "surah_number": 56,
    "verse_number": 51,
    "content": "پھر تم اے جھٹلانے والے گمرا ہو!"
  },
  {"surah_number": 56, "verse_number": 52, "content": "تھوہر کے درخت کھاؤ گے"},
  {"surah_number": 56, "verse_number": 53, "content": "اور اسی سے پیٹ بھرو گے"},
  {
    "surah_number": 56,
    "verse_number": 54,
    "content": "اور اس پر کھولتا ہوا پانی پیو گے"
  },
  {
    "surah_number": 56,
    "verse_number": 55,
    "content": "اور پیو گے بھی تو اس طرح جیسے پیاسے اونٹ پیتے ہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 56,
    "content": "جزا کے دن یہ ان کی ضیافت ہوگی"
  },
  {
    "surah_number": 56,
    "verse_number": 57,
    "content":
        "ہم نے تم کو (پہلی بار بھی تو) پیدا کیا ہے تو تم (دوبارہ اُٹھنے کو) کیوں سچ نہیں سمجھتے؟"
  },
  {
    "surah_number": 56,
    "verse_number": 58,
    "content": "دیکھو تو کہ جس (نطفے) کو تم (عورتوں کے رحم میں) ڈالتے ہو"
  },
  {
    "surah_number": 56,
    "verse_number": 59,
    "content": "کیا تم اس (سے انسان) کو بناتے ہو یا ہم بناتے ہیں؟"
  },
  {
    "surah_number": 56,
    "verse_number": 60,
    "content": "ہم نے تم میں مرنا ٹھہرا دیا ہے اور ہم اس (بات) سے عاجز نہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 61,
    "content":
        "کہ تمہاری طرح کے اور لوگ تمہاری جگہ لے آئیں اور تم کو ایسے جہان میں جس کو تم نہیں جانتے پیدا کر دیں"
  },
  {
    "surah_number": 56,
    "verse_number": 62,
    "content": "اور تم نے پہلی پیدائش تو جان ہی لی ہے۔ پھر تم سوچتے کیوں نہیں؟"
  },
  {
    "surah_number": 56,
    "verse_number": 63,
    "content": "بھلا دیکھو تو کہ جو کچھ تم بوتے ہو"
  },
  {
    "surah_number": 56,
    "verse_number": 64,
    "content": "تو کیا تم اسے اُگاتے ہو یا ہم اُگاتے ہیں؟"
  },
  {
    "surah_number": 56,
    "verse_number": 65,
    "content": "اگر ہم چاہیں تو اسے چورا چورا کردیں اور تم باتیں بناتے رہ جاؤ"
  },
  {
    "surah_number": 56,
    "verse_number": 66,
    "content": "(کہ ہائے) ہم تو مفت تاوان میں پھنس گئے"
  },
  {"surah_number": 56, "verse_number": 67, "content": "بلکہ ہم ہیں ہی بےنصیب"},
  {
    "surah_number": 56,
    "verse_number": 68,
    "content": "بھلا دیکھو تو کہ جو پانی تم پیتے ہو"
  },
  {
    "surah_number": 56,
    "verse_number": 69,
    "content": "کیا تم نے اس کو بادل سے نازل کیا ہے یا ہم نازل کرتے ہیں؟"
  },
  {
    "surah_number": 56,
    "verse_number": 70,
    "content": "اگر ہم چاہیں تو ہم اسے کھاری کردیں پھر تم شکر کیوں نہیں کرتے؟"
  },
  {
    "surah_number": 56,
    "verse_number": 71,
    "content": "بھلا دیکھو تو جو آگ تم درخت سے نکالتے ہو"
  },
  {
    "surah_number": 56,
    "verse_number": 72,
    "content": "کیا تم نے اس کے درخت کو پیدا کیا ہے یا ہم پیدا کرتے ہیں؟"
  },
  {
    "surah_number": 56,
    "verse_number": 73,
    "content": "ہم نے اسے یاد دلانے اور مسافروں کے برتنے کو بنایا ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 74,
    "content": "تو تم اپنے پروردگار بزرگ کے نام کی تسبیح کرو"
  },
  {
    "surah_number": 56,
    "verse_number": 75,
    "content": "ہمیں تاروں کی منزلوں کی قسم"
  },
  {
    "surah_number": 56,
    "verse_number": 76,
    "content": "اور اگر تم سمجھو تو یہ بڑی قسم ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 77,
    "content": "کہ یہ بڑے رتبے کا قرآن ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 78,
    "content": "(جو) کتاب محفوظ میں (لکھا ہوا ہے)"
  },
  {
    "surah_number": 56,
    "verse_number": 79,
    "content": "اس کو وہی ہاتھ لگاتے ہیں جو پاک ہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 80,
    "content": "پروردگار عالم کی طرف سے اُتارا گیا ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 81,
    "content": "کیا تم اس کلام سے انکار کرتے ہو؟"
  },
  {
    "surah_number": 56,
    "verse_number": 82,
    "content": "اور اپنا وظیفہ یہ بناتے ہو کہ (اسے) جھٹلاتے ہو"
  },
  {
    "surah_number": 56,
    "verse_number": 83,
    "content": "بھلا جب روح گلے میں آ پہنچتی ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 84,
    "content": "اور تم اس وقت کی (حالت کو) دیکھا کرتے ہو"
  },
  {
    "surah_number": 56,
    "verse_number": 85,
    "content":
        "اور ہم اس (مرنے والے) سے تم سے بھی زیادہ نزدیک ہوتے ہیں لیکن تم کو نظر نہیں آتے"
  },
  {
    "surah_number": 56,
    "verse_number": 86,
    "content": "پس اگر تم کسی کے بس میں نہیں ہو"
  },
  {
    "surah_number": 56,
    "verse_number": 87,
    "content": "تو اگر سچے ہو تو روح کو پھیر کیوں نہیں لیتے؟"
  },
  {
    "surah_number": 56,
    "verse_number": 88,
    "content": "پھر اگر وہ (خدا کے) مقربوں میں سے ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 89,
    "content": "تو (اس کے لئے) آرام اور خوشبودار پھول اور نعمت کے باغ ہیں"
  },
  {
    "surah_number": 56,
    "verse_number": 90,
    "content": "اور اگر وہ دائیں ہاتھ والوں میں سے ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 91,
    "content": "تو (کہا جائے گا کہ) تجھ پر داہنے ہاتھ والوں کی طرف سے سلام"
  },
  {
    "surah_number": 56,
    "verse_number": 92,
    "content": "اور اگر وہ جھٹلانے والے گمراہوں میں سے ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 93,
    "content": "تو (اس کے لئے) کھولتے پانی کی ضیافت ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 94,
    "content": "اور جہنم میں داخل کیا جانا"
  },
  {
    "surah_number": 56,
    "verse_number": 95,
    "content": "یہ (داخل کیا جانا یقیناً صحیح یعنی) حق الیقین ہے"
  },
  {
    "surah_number": 56,
    "verse_number": 96,
    "content": "تو تم اپنے پروردگار بزرگ کے نام کی تسبیح کرتے رہو"
  },
  {
    "surah_number": 57,
    "verse_number": 1,
    "content":
        "جو مخلوق آسمانوں اور زمین میں ہے خدا کی تسبیح کرتی ہے۔ اور وہ غالب (اور) حکمت والا ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 2,
    "content":
        "آسمانوں اور زمین کی بادشاہی اسی کی ہے۔ (وہی) زندہ کرتا اور مارتا ہے۔ اور وہ ہر چیز پر قادر ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 3,
    "content":
        "وہ (سب سے) پہلا اور (سب سے) پچھلا اور (اپنی قدرتوں سے سب پر) ظاہر اور (اپنی ذات سے) پوشیدہ ہے اور وہ تمام چیزوں کو جانتا ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 4,
    "content":
        "وہی ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھر عرش پر جا ٹھہرا۔ جو چیز زمین میں داخل ہوتی اور جو اس سے نکلتی ہے اور جو آسمان سے اُترتی اور جو اس کی طرف چڑھتی ہے سب اس کو معلوم ہے۔ اور تم جہاں کہیں ہو وہ تمہارے ساتھ ہے۔ اور جو کچھ تم کرتے ہو خدا اس کو دیکھ رہا ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 5,
    "content":
        "آسمانوں اور زمین کی بادشاہی اسی کی ہے۔ اور سب امور اسی کی طرف رجوع ہوتے ہیں"
  },
  {
    "surah_number": 57,
    "verse_number": 6,
    "content":
        "رات کو دن میں داخل کرتا اور دن کو رات میں داخل کرتا ہے۔ اور وہ دلوں کے بھیدوں تک سے واقف ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 7,
    "content":
        "تو) خدا پر اور اس کے رسول پر ایمان لاؤ اور جس (مال) میں اس نے تم کو (اپنا) نائب بنایا ہے اس میں سے خرچ کرو۔ جو لوگ تم میں سے ایمان لائے اور (مال) خرچ کرتے رہے ان کے لئے بڑا ثواب ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 8,
    "content":
        "اور تم کیسے لوگ ہو کہ خدا پر ایمان نہیں لاتے۔ حالانکہ (اس کے) پیغمبر تمہیں بلا رہے ہیں کہ اپنے پروردگار پر ایمان لاؤ اور اگر تم کو باور ہو تو وہ تم سے (اس کا) عہد بھی لے چکا ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 9,
    "content":
        "وہی تو ہے جو اپنے بندے پر واضح (المطالب) آیتیں نازل کرتا ہے تاکہ تم کو اندھیروں میں سے نکال کر روشنی میں لائے۔ بےشک خدا تم پر نہایت شفقت کرنے والا (اور) مہربان ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 10,
    "content":
        "اور تم کو کیا ہوا ہے کہ خدا کے رستے میں خرچ نہیں کرتے حالانکہ آسمانوں اور زمین کی وراثت خدا ہی کی ہے۔ جس شخص نے تم میں سے فتح (مکہ) سے پہلے خرچ کیا اور لڑائی کی وہ (اور جس نے یہ کام پیچھے کئے وہ) برابر نہیں۔ ان کا درجہ ان لوگوں سے کہیں بڑھ کر ہے جنہوں نے بعد میں خرچ (اموال) اور (کفار سے) جہاد وقتال کیا۔ اور خدا نے سب سے (ثواب) نیک (کا) وعدہ تو کیا ہے۔ اور جو کام تم کرتے ہو خدا ان سے واقف ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 11,
    "content":
        "کون ہے جو خدا کو (نیت) نیک (اور خلوص سے) قرض دے تو وہ اس کو اس سے دگنا کرے اور اس کے لئے عزت کا صلہ (یعنی جنت) ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 12,
    "content":
        "جس دن تم مومن مردوں اور مومن عورتوں کو دیکھو گے کہ ان (کے ایمان) کا نور ان کے آگے آگے اور داہنی طرف چل رہا ہے (تو ان سے کہا جائے گا کہ) تم کو بشارت ہو (کہ آج تمہارے لئے) باغ ہیں جن کے تلے نہریں بہہ رہی ہیں ان میں ہمیشہ رہو گے۔ یہی بڑی کامیابی ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 13,
    "content":
        "اُس دن منافق مرد اور منافق عورتیں مومنوں سے کہیں گے کہ ہماری طرف سے (شفقت) کیجیئے کہ ہم بھی تمہارے نور سے روشنی حاصل کریں۔ تو ان سے کہا جائے گا کہ پیچھے کو لوٹ جاؤ اور (وہاں) نور تلاش کرو۔ پھر ان کے بیچ میں ایک دیوار کھڑی کر دی جائے گی۔ جس میں ایک دروازہ ہوگا جو اس کی جانب اندرونی ہے اس میں تو رحمت ہے اور جو جانب بیرونی ہے اس طرف عذاب (واذیت)"
  },
  {
    "surah_number": 57,
    "verse_number": 14,
    "content":
        "تو منافق لوگ مومنوں سے کہیں گے کہ کیا ہم (دنیا میں) تمہارے ساتھ نہ تھے وہ کہیں گے کیوں نہیں تھے۔ لیکن تم نے خود اپنے تئیں بلا میں ڈالا اور (ہمارے حق میں حوادث کے) منتظر رہے اور (اسلام میں) شک کیا اور (لاطائل) آرزوؤں نے تم کو دھوکہ دیا یہاں تک کہ خدا کا حکم آ پہنچا اور خدا کے بارے میں تم کو (شیطان) دغاباز دغا دیتا رہا"
  },
  {
    "surah_number": 57,
    "verse_number": 15,
    "content":
        "تو آج تم سے معاوضہ نہیں لیا جائے گا اور نہ (وہ) کافروں ہی سے (قبول کیا جائے گا) تم سب کا ٹھکانا دوزخ ہے۔ (کہ) وہی تمہارے لائق ہے اور وہ بری جگہ ہے"
  },
  {
    "surah_number": 57,
    "verse_number": 16,
    "content":
        "کیا ابھی تک مومنوں کے لئے اس کا وقت نہیں آیا کہ خدا کی یاد کرنے کے وقت اور (قرآن) جو (خدائے) برحق (کی طرف) سے نازل ہوا ہے اس کے سننے کے وقت ان کے دل نرم ہوجائیں اور وہ ان لوگوں کی طرف نہ ہوجائیں جن کو (ان سے) پہلے کتابیں دی گئی تھیں۔ پھر ان پر زمان طویل گزر گیا تو ان کے دل سخت ہوگئے۔ اور ان میں سے اکثر نافرمان ہیں"
