    "surah_number": 74,
    "verse_number": 43,
    "content": "وہ جواب دیں گے کہ ہم نماز نہیں پڑھتے تھے"
  },
  {
    "surah_number": 74,
    "verse_number": 44,
    "content": "اور نہ فقیروں کو کھانا کھلاتے تھے"
  },
  {
    "surah_number": 74,
    "verse_number": 45,
    "content": "اور اہل باطل کے ساتھ مل کر (حق سے) انکار کرتے تھے"
  },
  {
    "surah_number": 74,
    "verse_number": 46,
    "content": "اور روز جزا کو جھٹلاتے تھے"
  },
  {
    "surah_number": 74,
    "verse_number": 47,
    "content": "یہاں تک کہ ہمیں موت آگئی"
  },
  {
    "surah_number": 74,
    "verse_number": 48,
    "content":
        "(تو اس حال میں) سفارش کرنے والوں کی سفارش ان کے حق میں کچھ فائدہ نہ دے گی"
  },
  {
    "surah_number": 74,
    "verse_number": 49,
    "content": "ان کو کیا ہوا ہے کہ نصیحت سے روگرداں ہو رہے ہیں"
  },
  {
    "surah_number": 74,
    "verse_number": 50,
    "content": "گویا گدھے ہیں کہ بدک جاتے ہیں"
  },
  {
    "surah_number": 74,
    "verse_number": 51,
    "content": "(یعنی) شیر سے ڈر کر بھاگ جاتے ہیں"
  },
  {
    "surah_number": 74,
    "verse_number": 52,
    "content":
        "اصل یہ ہے کہ ان میں سے ہر شخص یہ چاہتا ہے کہ اس کے پاس کھلی ہوئی کتاب آئے"
  },
  {
    "surah_number": 74,
    "verse_number": 53,
    "content": "ایسا ہرگز نہیں ہوگا۔ حقیقت یہ ہے کہ ان کو آخرت کا خوف ہی نہیں"
  },
  {
    "surah_number": 74,
    "verse_number": 54,
    "content": "کچھ شک نہیں کہ یہ نصیحت ہے"
  },
  {
    "surah_number": 74,
    "verse_number": 55,
    "content": "تو جو چاہے اسے یاد رکھے"
  },
  {
    "surah_number": 74,
    "verse_number": 56,
    "content":
        "اور یاد بھی تب ہی رکھیں گے جب خدا چاہے۔ وہی ڈرنے کے لائق اور بخشش کا مالک ہے"
  },
  {"surah_number": 75, "verse_number": 1, "content": "ہم کو روز قیامت کی قسم"},
  {
    "surah_number": 75,
    "verse_number": 2,
    "content": "اور نفس لوامہ کی (کہ سب لوگ اٹھا کر) کھڑے کئے جائیں گے"
  },
  {
    "surah_number": 75,
    "verse_number": 3,
    "content":
        "کیا انسان یہ خیال کرتا ہے کہ ہم اس کی (بکھری ہوئی) ہڈیاں اکٹھی نہیں کریں گے؟"
  },
  {
    "surah_number": 75,
    "verse_number": 4,
    "content":
        "ضرور کریں گے (اور) ہم اس بات پر قادر ہیں کہ اس کی پور پور درست کردیں"
  },
  {
    "surah_number": 75,
    "verse_number": 5,
    "content": "مگر انسان چاہتا ہے کہ آگے کو خود سری کرتا جائے"
  },
  {
    "surah_number": 75,
    "verse_number": 6,
    "content": "پوچھتا ہے کہ قیامت کا دن کب ہوگا؟"
  },
  {"surah_number": 75, "verse_number": 7, "content": "جب آنکھیں چندھیا جائیں"},
  {"surah_number": 75, "verse_number": 8, "content": "اور چاند گہنا جائے"},
  {
    "surah_number": 75,
    "verse_number": 9,
    "content": "اور سورج اور چاند جمع کردیئے جائیں"
  },
  {
    "surah_number": 75,
    "verse_number": 10,
    "content": "اس دن انسان کہے گا کہ (اب) کہاں بھاگ جاؤں؟"
  },
  {"surah_number": 75, "verse_number": 11, "content": "بےشک کہیں پناہ نہیں"},
  {
    "surah_number": 75,
    "verse_number": 12,
    "content": "اس روز پروردگار ہی کے پاس ٹھکانا ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 13,
    "content":
        "اس دن انسان کو جو (عمل) اس نے آگے بھیجے اور پیچھے چھوڑے ہوں گے سب بتا دیئے جائیں گے"
  },
  {
    "surah_number": 75,
    "verse_number": 14,
    "content": "بلکہ انسان آپ اپنا گواہ ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 15,
    "content": "اگرچہ عذر ومعذرت کرتا رہے"
  },
  {
    "surah_number": 75,
    "verse_number": 16,
    "content":
        "اور (اے محمدﷺ) وحی کے پڑھنے کے لئے اپنی زبان نہ چلایا کرو کہ اس کو جلد یاد کرلو"
  },
  {
    "surah_number": 75,
    "verse_number": 17,
    "content": "اس کا جمع کرنا اور پڑھانا ہمارے ذمے ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 18,
    "content":
        "جب ہم وحی پڑھا کریں تو تم (اس کو سنا کرو اور) پھر اسی طرح پڑھا کرو"
  },
  {
    "surah_number": 75,
    "verse_number": 19,
    "content": "پھر اس (کے معانی) کا بیان بھی ہمارے ذمے ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 20,
    "content": "مگر (لوگو) تم دنیا کو دوست رکھتے ہو"
  },
  {
    "surah_number": 75,
    "verse_number": 21,
    "content": "اور آخرت کو ترک کئے دیتے ہو"
  },
  {
    "surah_number": 75,
    "verse_number": 22,
    "content": "اس روز بہت سے منہ رونق دار ہوں گے"
  },
  {
    "surah_number": 75,
    "verse_number": 23,
    "content": "اور) اپنے پروردگار کے محو دیدار ہوں گے"
  },
  {
    "surah_number": 75,
    "verse_number": 24,
    "content": "اور بہت سے منہ اس دن اداس ہوں گے"
  },
  {
    "surah_number": 75,
    "verse_number": 25,
    "content": "خیال کریں گے کہ ان پر مصیبت واقع ہونے کو ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 26,
    "content": "دیکھو جب جان گلے تک پہنچ جائے"
  },
  {
    "surah_number": 75,
    "verse_number": 27,
    "content": "اور لوگ کہنے لگیں (اس وقت) کون جھاڑ پھونک کرنے والا ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 28,
    "content": "اور اس (جان بلب) نے سمجھا کہ اب سب سے جدائی ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 29,
    "content": "اور پنڈلی سے پنڈلی لپٹ جائے"
  },
  {
    "surah_number": 75,
    "verse_number": 30,
    "content": "اس دن تجھ کو اپنے پروردگار کی طرف چلنا ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 31,
    "content":
        "تو اس (ناعاقبت) اندیش نے نہ تو (کلام خدا) کی تصدیق کی نہ نماز پڑھی"
  },
  {
    "surah_number": 75,
    "verse_number": 32,
    "content": "بلکہ جھٹلایا اور منہ پھیر لیا"
  },
  {
    "surah_number": 75,
    "verse_number": 33,
    "content": "پھر اپنے گھر والوں کے پاس اکڑتا ہوا چل دیا"
  },
  {
    "surah_number": 75,
    "verse_number": 34,
    "content": "افسوس ہے تجھ پر پھر افسوس ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 35,
    "content": "پھر افسوس ہے تجھ پر پھر افسوس ہے"
  },
  {
    "surah_number": 75,
    "verse_number": 36,
    "content": "کیا انسان خیال کرتا ہے کہ یوں ہی چھوڑ دیا جائے گا؟"
  },
  {
    "surah_number": 75,
    "verse_number": 37,
    "content": "کیا وہ منی کا جو رحم میں ڈالی جاتی ہے ایک قطرہ نہ تھا؟"
  },
  {
    "surah_number": 75,
    "verse_number": 38,
    "content":
        "پھر لوتھڑا ہوا پھر (خدا نے) اس کو بنایا پھر (اس کے اعضا کو) درست کیا"
  },
  {
    "surah_number": 75,
    "verse_number": 39,
    "content": "پھر اس کی دو قسمیں بنائیں (ایک) مرد اور (ایک) عورت"
  },
  {
    "surah_number": 75,
    "verse_number": 40,
    "content": "کیا اس خالق کو اس بات پر قدرت نہیں کہ مردوں کو جلا اُٹھائے؟"
  },
  {
    "surah_number": 76,
    "verse_number": 1,
    "content":
        "بےشک انسان پر زمانے میں ایک ایسا وقت بھی آچکا ہے کہ وہ کوئی چیز قابل ذکر نہ تھی"
  },
  {
    "surah_number": 76,
    "verse_number": 2,
    "content":
        "ہم نے انسان کو نطفہٴ مخلوط سے پیدا کیا تاکہ اسے آزمائیں تو ہم نے اس کو سنتا دیکھتا بنایا"
  },
  {
    "surah_number": 76,
    "verse_number": 3,
    "content":
        "(اور) اسے رستہ بھی دکھا دیا۔ (اب) وہ خواہ شکرگزار ہو خواہ ناشکرا"
  },
  {
    "surah_number": 76,
    "verse_number": 4,
    "content": "ہم نے کافروں کے لئے زنجیر اور طوق اور دہکتی آگ تیار کر رکھی ہے"
  },
  {
    "surah_number": 76,
    "verse_number": 5,
    "content":
        "جو نیکو کار ہیں اور وہ ایسی شراب نوش جان کریں گے جس میں کافور کی آمیزش ہوگی"
  },
  {
    "surah_number": 76,
    "verse_number": 6,
    "content":
        "یہ ایک چشمہ ہے جس میں سے خدا کے بندے پئیں گے اور اس میں سے (چھوٹی چھوٹی) نہریں نکالیں گے"
  },
  {
    "surah_number": 76,
    "verse_number": 7,
    "content":
        "یہ لوگ نذریں پوری کرتے ہیں اور اس دن سے جس کی سختی پھیل رہی ہوگی خوف رکھتے ہیں"
  },
  {
    "surah_number": 76,
    "verse_number": 8,
    "content":
        "اور باوجود یہ کہ ان کو خود طعام کی خواہش (اور حاجت) ہے فقیروں اور یتیموں اور قیدیوں کو کھلاتے ہیں"
  },
  {
    "surah_number": 76,
    "verse_number": 9,
    "content":
        "(اور کہتے ہیں کہ) ہم تم کو خالص خدا کے لئے کھلاتے ہیں۔ نہ تم سے عوض کے خواستگار ہیں نہ شکرگزاری کے (طلبگار)"
  },
  {
    "surah_number": 76,
    "verse_number": 10,
    "content":
        "ہم کو اپنے پروردگار سے اس دن کا ڈر لگتا ہے (جو چہروں کو) کریہہ المنظر اور (دلوں کو) سخت (مضطر کر دینے والا) ہے"
  },
  {
    "surah_number": 76,
    "verse_number": 11,
    "content":
        "تو خدا ان کو اس دن کی سختی سے بچالے گا اور تازگی اور خوش دلی عنایت فرمائے گا"
  },
  {
    "surah_number": 76,
    "verse_number": 12,
    "content":
        "اور ان کے صبر کے بدلے ان کو بہشت (کے باغات) اور ریشم (کے ملبوسات) عطا کرے گا"
  },
  {
    "surah_number": 76,
    "verse_number": 13,
    "content":
        "ان میں وہ تختوں پر تکیے لگائے بیٹھے ہوں گے۔ وہاں نہ دھوپ (کی حدت) دیکھیں گے نہ سردی کی شدت"
  },
  {
    "surah_number": 76,
    "verse_number": 14,
    "content":
        "ان سے (ثمردار شاخیں اور) ان کے سائے قریب ہوں گے اور میوؤں کے گچھے جھکے ہوئے لٹک رہے ہوں گے"
  },
  {
    "surah_number": 76,
    "verse_number": 15,
    "content":
        "خدام) چاندی کے باسن لئے ہوئے ان کے اردگرد پھریں گے اور شیشے کے (نہایت شفاف) گلاس"
  },
  {
    "surah_number": 76,
    "verse_number": 16,
    "content": "اور شیشے بھی چاندی کے جو ٹھیک اندازے کے مطابق بنائے گئے ہیں"
  },
  {
    "surah_number": 76,
    "verse_number": 17,
    "content":
        "اور وہاں ان کو ایسی شراب (بھی) پلائی جائے گی جس میں سونٹھ کی آمیزش ہوگی"
  },
  {
    "surah_number": 76,
    "verse_number": 18,
    "content": "یہ بہشت میں ایک چشمہ ہے جس کا نام سلسبیل ہے"
  },
  {
    "surah_number": 76,
    "verse_number": 19,
    "content":
        "اور ان کے پاس لڑکے آتے جاتے ہوں گے جو ہمیشہ (ایک ہی حالت پر) رہیں گے۔ جب تم ان پر نگاہ ڈالو تو خیال کرو کہ بکھرے ہوئے موتی ہیں"
  },
  {
    "surah_number": 76,
    "verse_number": 20,
    "content":
        "اور بہشت میں (جہاں) آنکھ اٹھاؤ گے کثرت سے نعمت اور عظیم (الشان) سلطنت دیکھو گے"
  },
  {
    "surah_number": 76,
    "verse_number": 21,
    "content":
        "ان (کے بدنوں) پر دیبا سبز اور اطلس کے کپڑے ہوں گے۔ اور انہیں چاندی کے کنگن پہنائے جائیں گے اور ان کا پروردگار ان کو نہایت پاکیزہ شراب پلائے گا"
  },
  {
    "surah_number": 76,
    "verse_number": 22,
    "content": "یہ تمہارا صلہ اور تمہاری کوشش (خدا کے ہاں) مقبول ہوئی"
  },
  {
    "surah_number": 76,
    "verse_number": 23,
    "content": "اے محمد (ﷺ) ہم نے تم پر قرآن آہستہ آہستہ نازل کیا ہے"
  },
  {
    "surah_number": 76,
    "verse_number": 24,
    "content":
        "تو اپنے پروردگار کے حکم کے مطابق صبر کئے رہو اور ان لوگوں میں سے کسی بد عمل اور ناشکرے کا کہا نہ مانو"
  },
  {
    "surah_number": 76,
    "verse_number": 25,
    "content": "اور صبح وشام اپنے پروردگار کا نام لیتے رہو"
  },
  {
    "surah_number": 76,
    "verse_number": 26,
    "content": "اور رات کو بڑی رات تک سجدے کرو اور اس کی پاکی بیان کرتے رہو"
  },
  {
    "surah_number": 76,
    "verse_number": 27,
    "content":
        "یہ لوگ دنیا کو دوست رکھتے ہیں اور (قیامت کے) بھاری دن کو پس پشت چھوڑے دیتے ہیں"
  },
  {
    "surah_number": 76,
    "verse_number": 28,
    "content":
        "ہم نے ان کو پیدا کیا اور ان کے مقابل کو مضبوط بنایا۔ اور اگر ہم چاہیں تو ان کے بدلے ان ہی کی طرح اور لوگ لے آئیں"
  },
  {
    "surah_number": 76,
    "verse_number": 29,
    "content":
        "یہ تو نصیحت ہے۔ جو چاہے اپنے پروردگار کی طرف پہنچنے کا رستہ اختیار کرے"
  },
  {
    "surah_number": 76,
    "verse_number": 30,
    "content":
        "اور تم کچھ بھی نہیں چاہ سکتے مگر جو خدا کو منظور ہو۔ بےشک خدا جاننے والا حکمت والا ہے"
  },
  {
    "surah_number": 76,
    "verse_number": 31,
    "content":
        "جس کو چاہتا ہے اپنی رحمت میں داخل کرلیتا ہے اور ظالموں کے لئے اس نے دکھ دینے والا عذاب تیار کر رکھا ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 1,
    "content": "ہواؤں کی قسم جو نرم نرم چلتی ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 2,
    "content": "پھر زور پکڑ کر جھکڑ ہو جاتی ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 3,
    "content": "اور (بادلوں کو) پھاڑ کر پھیلا دیتی ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 4,
    "content": "پھر ان کو پھاڑ کر جدا جدا کر دیتی ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 5,
    "content": "پھر فرشتوں کی قسم جو وحی لاتے ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 6,
    "content": "تاکہ عذر (رفع) کردیا جائے یا ڈر سنا دیا جائے"
  },
  {
    "surah_number": 77,
    "verse_number": 7,
    "content": "کہ جس بات کا تم سے وعدہ کیا جاتا ہے وہ ہو کر رہے گی"
  },
  {
    "surah_number": 77,
    "verse_number": 8,
    "content": "جب تاروں کی چمک جاتی رہے"
  },
  {"surah_number": 77, "verse_number": 9, "content": "اور جب آسمان پھٹ جائے"},
  {
    "surah_number": 77,
    "verse_number": 10,
    "content": "اور جب پہاڑ اُڑے اُڑے پھریں"
  },
  {
    "surah_number": 77,
    "verse_number": 11,
    "content": "اور جب پیغمبر فراہم کئے جائیں"
  },
  {
    "surah_number": 77,
    "verse_number": 12,
    "content": "بھلا (ان امور میں) تاخیر کس دن کے لئے کی گئی؟"
  },
  {"surah_number": 77, "verse_number": 13, "content": "فیصلے کے دن کے لئے"},
  {
    "surah_number": 77,
    "verse_number": 14,
    "content": "اور تمہیں کیا خبر کہ فیصلے کا دن کیا ہے؟"
  },
  {
    "surah_number": 77,
    "verse_number": 15,
    "content": "اس دن جھٹلانے والوں کے لئے خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 16,
    "content": "کیا ہم نے پہلے لوگوں کو ہلاک نہیں کر ڈالا"
  },
  {
    "surah_number": 77,
    "verse_number": 17,
    "content": "پھر ان پچھلوں کو بھی ان کے پیچھے بھیج دیتے ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 18,
    "content": "ہم گنہگاروں کے ساتھ ایسا ہی کیا کرتے ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 19,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 20,
    "content": "کیا ہم نے تم کو حقیر پانی سے نہیں پیدا کیا؟"
  },
  {
    "surah_number": 77,
    "verse_number": 21,
    "content": "اس کو ایک محفوظ جگہ میں رکھا"
  },
  {"surah_number": 77, "verse_number": 22, "content": "ایک وقت معین تک"},
  {
    "surah_number": 77,
    "verse_number": 23,
    "content": "پھر اندازہ مقرر کیا اور ہم کیا ہی خوب اندازہ مقرر کرنے والے ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 24,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 25,
    "content": "کیا ہم نے زمین کو سمیٹنے والی نہیں بنایا"
  },
  {
    "surah_number": 77,
    "verse_number": 26,
    "content": "یعنی) زندوں اور مردوں کو"
  },
  {
    "surah_number": 77,
    "verse_number": 27,
    "content":
        "(بنایا) اور اس پر اونچے اونچے پہاڑ رکھ دیئے اور تم لوگوں کو میٹھا پانی پلایا"
  },
  {
    "surah_number": 77,
    "verse_number": 28,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 29,
    "content": "جس چیز کو تم جھٹلایا کرتے تھے۔ (اب) اس کی طرف چلو"
  },
  {
    "surah_number": 77,
    "verse_number": 30,
    "content": "(یعنی) اس سائے کی طرف چلو جس کی تین شاخیں ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 31,
    "content": "نہ ٹھنڈی چھاؤں اور نہ لپٹ سے بچاؤ"
  },
  {
    "surah_number": 77,
    "verse_number": 32,
    "content": "اس سے (آگ کی اتنی اتنی بڑی) چنگاریاں اُڑتی ہیں جیسے محل"
  },
  {
    "surah_number": 77,
    "verse_number": 33,
    "content": "گویا زرد رنگ کے اونٹ ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 34,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 35,
    "content": "یہ وہ دن ہے کہ (لوگ) لب تک نہ ہلا سکیں گے"
  },
  {
    "surah_number": 77,
    "verse_number": 36,
    "content": "اور نہ ان کو اجازت دی جائے گی کہ عذر کرسکیں"
  },
  {
    "surah_number": 77,
    "verse_number": 37,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 38,
    "content":
        "یہی فیصلے کا دن ہے (جس میں) ہم نے تم کو اور پہلے لوگوں کو جمع کیا ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 39,
    "content": "اگر تم کو کوئی داؤں آتا ہو تو مجھ سے کر لو"
  },
  {
    "surah_number": 77,
    "verse_number": 40,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 41,
    "content": "بےشک پرہیزگار سایوں اور چشموں میں ہوں گے"
  },
  {
    "surah_number": 77,
    "verse_number": 42,
    "content": "اور میؤوں میں جو ان کو مرغوب ہوں"
  },
  {
    "surah_number": 77,
    "verse_number": 43,
    "content": "اور جو عمل تم کرتے رہے تھے ان کے بدلے میں مزے سے کھاؤ اور پیو"
  },
  {
    "surah_number": 77,
    "verse_number": 44,
    "content": "ہم نیکو کاروں کو ایسا ہی بدلہ دیا کرتے ہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 45,
    "content": "اس دن جھٹلانے والوں کی خرابی ہوگی"
  },
  {
    "surah_number": 77,
    "verse_number": 46,
    "content":
        "(اے جھٹلانے والو!) تم کسی قدر کھا لو اور فائدے اُٹھا لو تم بےشک گنہگار ہو"
  },
  {
    "surah_number": 77,
    "verse_number": 47,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 48,
    "content": "اور جب ان سے کہا جاتا ہے کہ (خدا کے آگے) جھکو تو جھکتے نہیں"
  },
  {
    "surah_number": 77,
    "verse_number": 49,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 77,
    "verse_number": 50,
    "content": "اب اس کے بعد یہ کون سی بات پر ایمان لائیں گے؟"
  },
  {
    "surah_number": 78,
    "verse_number": 1,
    "content": "(یہ) لوگ کس چیز کی نسبت پوچھتے ہیں؟"
  },
  {"surah_number": 78, "verse_number": 2, "content": "(کیا) بڑی خبر کی نسبت؟"},
  {
    "surah_number": 78,
    "verse_number": 3,
    "content": "جس میں یہ اختلاف کر رہے ہیں"
  },
  {
    "surah_number": 78,
    "verse_number": 4,
    "content": "دیکھو یہ عنقریب جان لیں گے"
  },
  {
    "surah_number": 78,
    "verse_number": 5,
    "content": "پھر دیکھو یہ عنقریب جان لیں گے"
  },
  {
    "surah_number": 78,
    "verse_number": 6,
    "content": "کیا ہم نے زمین کو بچھونا نہیں بنایا"
  },
  {
    "surah_number": 78,
    "verse_number": 7,
    "content": "اور پہاڑوں کو (ا س کی) میخیں (نہیں ٹھہرایا؟)"
  },
  {
    "surah_number": 78,
    "verse_number": 8,
    "content": "(بے شک بنایا) اور تم کو جوڑا جوڑابھی پیدا کیا"
  },
  {
    "surah_number": 78,
    "verse_number": 9,
    "content": "اور نیند کو تمہارے لیے (موجب) آرام بنایا"
  },
  {
    "surah_number": 78,
    "verse_number": 10,
    "content": "اور رات کو پردہ مقرر کیا"
  },
  {
    "surah_number": 78,
    "verse_number": 11,
    "content": "اور دن کو معاش (کا وقت) قرار دیا"
  },
  {
    "surah_number": 78,
    "verse_number": 12,
    "content": "اور تمہارے اوپر سات مضبوط (آسمان) بنائے"
  },
  {
    "surah_number": 78,
    "verse_number": 13,
    "content": "اور (آفتاب کا) روشن چراغ بنایا"
  },
  {
    "surah_number": 78,
    "verse_number": 14,
    "content": "اور نچڑتے بادلوں سے موسلا دھار مینہ برسایا"
  },
  {
    "surah_number": 78,
    "verse_number": 15,
    "content": "تاکہ اس سے اناج اور سبزہ پیدا کریں"
  },
  {"surah_number": 78, "verse_number": 16, "content": "اور گھنے گھنے باغ"},
  {
    "surah_number": 78,
    "verse_number": 17,
    "content": "بےشک فیصلہ کا دن مقرر ہے"
  },
  {
    "surah_number": 78,
    "verse_number": 18,
    "content": "جس دن صور پھونکا جائے گا تو تم لوگ غٹ کے غٹ آ موجود ہو گے"
  },
  {
    "surah_number": 78,
    "verse_number": 19,
    "content": "اور آسمان کھولا جائے گا تو (اس میں) دروازے ہو جائیں گے"
  },
  {
    "surah_number": 78,
    "verse_number": 20,
    "content": "اور پہاڑ چلائے جائیں گے تو وہ ریت ہو کر رہ جائیں گے"
  },
  {"surah_number": 78, "verse_number": 21, "content": "بےشک دوزخ گھات میں ہے"},
  {
    "surah_number": 78,
    "verse_number": 22,
    "content": "(یعنی) سرکشوں کا وہی ٹھکانہ ہے"
  },
  {
    "surah_number": 78,
    "verse_number": 23,
    "content": "اس میں وہ مدتوں پڑے رہیں گے"
  },
  {
    "surah_number": 78,
    "verse_number": 24,
    "content": "وہاں نہ ٹھنڈک کا مزہ چکھیں گے۔ نہ (کچھ) پینا (نصیب ہو گا)"
  },
  {
    "surah_number": 78,
    "verse_number": 25,
    "content": "مگر گرم پانی اور بہتی پیپ"
  },
  {"surah_number": 78, "verse_number": 26, "content": "(یہ) بدلہ ہے پورا پورا"},
  {
    "surah_number": 78,
    "verse_number": 27,
    "content": "یہ لوگ حساب (آخرت) کی امید ہی نہیں رکھتے تھے"
  },
  {
    "surah_number": 78,
    "verse_number": 28,
    "content": "اور ہماری آیتوں کو جھوٹ سمجھ کر جھٹلاتے رہتے تھے"
  },
  {
    "surah_number": 78,
    "verse_number": 29,
    "content": "اور ہم نے ہر چیز کو لکھ کر ضبط کر رکھا ہے"
  },
  {
    "surah_number": 78,
    "verse_number": 30,
    "content": "سو (اب) مزہ چکھو۔ ہم تم پر عذاب ہی بڑھاتے جائیں گے"
  },
  {
    "surah_number": 78,
    "verse_number": 31,
    "content": "بے شک پرہیز گاروں کے لیے کامیابی ہے"
  },
  {"surah_number": 78, "verse_number": 32, "content": "(یعنی) باغ اور انگور"},
  {
    "surah_number": 78,
    "verse_number": 33,
    "content": "اور ہم عمر نوجوان عورتیں"
  },
  {
    "surah_number": 78,
    "verse_number": 34,
    "content": "اور شراب کے چھلکتے ہوئے گلاس"
  },
  {
    "surah_number": 78,
    "verse_number": 35,
    "content": "وہاں نہ بیہودہ بات سنیں گے نہ جھوٹ (خرافات)"
  },
  {
    "surah_number": 78,
    "verse_number": 36,
    "content": "یہ تمہارے پروردگار کی طرف سے صلہ ہے انعام کثیر"
  },
  {
    "surah_number": 78,
    "verse_number": 37,
    "content":
        "وہ جو آسمانوں اور زمین اور جو ان دونوں میں ہے سب کا مالک ہے بڑا مہربان کسی کو اس سے بات کرنے کا یارا نہیں ہوگا"
  },
  {
    "surah_number": 78,
    "verse_number": 38,
    "content":
        "جس دن روح (الامین) اور فرشتے صف باندھ کر کھڑے ہوں گے تو کوئی بول نہ سکے گا مگر جس کو (خدائے رحمٰن) اجازت بخشے اور اس نے بات بھی درست کہی ہو"
  },
  {
    "surah_number": 78,
    "verse_number": 39,
    "content":
        "یہ دن برحق ہے۔ پس جو شخص چاہے اپنے پروردگار کے پاس ٹھکانہ بنا ئے"
  },
  {
    "surah_number": 78,
    "verse_number": 40,
    "content":
        "ہم نے تم کو عذاب سے جو عنقریب آنے والا ہے آگاہ کر دیا ہے جس دن ہر شخص ان (اعمال) کو جو اس نے آگے بھیجے ہوں گے دیکھ لے گا اور کافر کہے گا کہ اے کاش میں مٹی ہوتا"
  },
  {
    "surah_number": 79,
    "verse_number": 1,
    "content": "ان (فرشتوں) کی قسم جو ڈوب کر کھینچ لیتے ہیں"
  },
  {
    "surah_number": 79,
    "verse_number": 2,
    "content": "اور ان کی جو آسانی سے کھول دیتے ہیں"
  },
  {
    "surah_number": 79,
    "verse_number": 3,
    "content": "اور ان کی جو تیرتے پھرتے ہیں"
  },
  {
    "surah_number": 79,
    "verse_number": 4,
    "content": "پھر لپک کر آگے بڑھتے ہیں"
  },
  {
    "surah_number": 79,
    "verse_number": 5,
    "content": "پھر (دنیا کے) کاموں کا انتظام کرتے ہیں"
  },
  {
    "surah_number": 79,
    "verse_number": 6,
    "content": "(کہ وہ دن آ کر رہے گا) جس دن زمین کو بھونچال آئے گا"
  },
  {
    "surah_number": 79,
    "verse_number": 7,
    "content": "پھر اس کے پیچھے اور (بھونچال) آئے گا"
  },
  {
    "surah_number": 79,
    "verse_number": 8,
    "content": "اس دن (لوگوں) کے دل خائف ہو رہے ہوں گے"
  },
  {"surah_number": 79, "verse_number": 9, "content": "اور آنکھیں جھکی ہوئی"},
  {
    "surah_number": 79,
    "verse_number": 10,
    "content": "(کافر) کہتے ہیں کیا ہم الٹے پاؤں پھر لوٹ جائیں گے"
  },
  {
    "surah_number": 79,
    "verse_number": 11,
    "content": "بھلا جب ہم کھوکھلی ہڈیاں ہو جائیں گے (تو پھر زندہ کئے جائیں گے)"
  },
  {
    "surah_number": 79,
    "verse_number": 12,
    "content": "کہتے ہیں کہ یہ لوٹنا تو (موجب) زیاں ہے"
  },
  {
    "surah_number": 79,
    "verse_number": 13,
    "content": "وہ تو صرف ایک ڈانٹ ہوگی"
  },
  {
    "surah_number": 79,
    "verse_number": 14,
    "content": "اس وقت وہ (سب) میدان (حشر) میں آ جمع ہوں گے"
  },
  {
    "surah_number": 79,
    "verse_number": 15,
    "content": "بھلا تم کو موسیٰ کی حکایت پہنچی ہے"
  },
  {
    "surah_number": 79,
    "verse_number": 16,
    "content": "جب اُن کے پروردگار نے ان کو پاک میدان (یعنی) طویٰ میں پکارا"
  },
  {
    "surah_number": 79,
    "verse_number": 17,
    "content": "(اور حکم دیا) کہ فرعون کے پاس جاؤ وہ سرکش ہو رہا ہے"
  },
  {
    "surah_number": 79,
    "verse_number": 18,
    "content": "اور (اس سے) کہو کہ کیا تو چاہتا ہے کہ پاک ہو جائے؟"
  },
  {
    "surah_number": 79,
    "verse_number": 19,
    "content":
        "اور میں تجھے تیرے پروردگار کا رستہ بتاؤں تاکہ تجھ کو خوف (پیدا) ہو"
  },
  {
    "surah_number": 79,
    "verse_number": 20,
    "content": "غرض انہوں نے اس کو بڑی نشانی دکھائی"
  },
  {
    "surah_number": 79,
    "verse_number": 21,
    "content": "مگر اس نے جھٹلایا اور نہ مانا"
  },
  {
    "surah_number": 79,
    "verse_number": 22,
    "content": "پھر لوٹ گیا اور تدبیریں کرنے لگا"
  },
  {
    "surah_number": 79,
    "verse_number": 23,
    "content": "اور (لوگوں کو) اکٹھا کیا اور پکارا"
  },
  {
    "surah_number": 79,
    "verse_number": 24,
    "content": "کہنے لگا کہ تمہارا سب سے بڑا مالک میں ہوں"
  },
  {
    "surah_number": 79,
    "verse_number": 25,
    "content": "تو خدا نے اس کو دنیا اور آخرت (دونوں) کے عذاب میں پکڑ لیا"
  },
  {
    "surah_number": 79,
    "verse_number": 26,
    "content": "جو شخص (خدا سے) ڈر رکھتا ہے اس کے لیے اس (قصے) میں عبرت ہے"
  },
  {
    "surah_number": 79,
    "verse_number": 27,
    "content": "بھلا تمہارا بنانا آسان ہے یا آسمان کا؟ اسی نے اس کو بنایا"
  },
  {
    "surah_number": 79,
    "verse_number": 28,
    "content": "اس کی چھت کو اونچا کیا اور پھر اسے برابر کر دیا"
  },
  {
    "surah_number": 79,
    "verse_number": 29,
    "content": "اور اسی نے رات کو تاریک بنایا اور (دن کو) دھوپ نکالی"
  },
  {
    "surah_number": 79,
    "verse_number": 30,
    "content": "اور اس کے بعد زمین کو پھیلا دیا"
  },
  {
    "surah_number": 79,
    "verse_number": 31,
    "content": "اسی نے اس میں سے اس کا پانی نکالا اور چارا اگایا"
  },
  {
    "surah_number": 79,
    "verse_number": 32,
    "content": "اور اس پر پہاڑوں کابوجھ رکھ دیا"
  },
  {
    "surah_number": 79,
    "verse_number": 33,
    "content": "یہ سب کچھ تمہارے اور تمہارے چارپایوں کے فائدے کے لیے (کیا)"
  },
  {"surah_number": 79, "verse_number": 34, "content": "تو جب بڑی آفت آئے گی"},
  {
    "surah_number": 79,
    "verse_number": 35,
    "content": "اس دن انسان اپنے کاموں کو یاد کرے گا"
  },
  {
    "surah_number": 79,
    "verse_number": 36,
    "content": "اور دوزخ دیکھنے والے کے سامنے نکال کر رکھ دی جائے گی"
  },
  {"surah_number": 79, "verse_number": 37, "content": "تو جس نے سرکشی کی"},
  {
    "surah_number": 79,
    "verse_number": 38,
    "content": "اور دنیا کی زندگی کو مقدم سمجھا"
  },
  {"surah_number": 79, "verse_number": 39, "content": "اس کا ٹھکانہ دوزخ ہے"},
  {
    "surah_number": 79,
    "verse_number": 40,
    "content":
        "اور جو اپنے پروردگار کے سامنے کھڑے ہونے سے ڈرتا اور جی کو خواہشوں سے روکتا رہا"
  },
  {"surah_number": 79, "verse_number": 41, "content": "اس کا ٹھکانہ بہشت ہے"},
  {
    "surah_number": 79,
    "verse_number": 42,
    "content":
        "(اے پیغمبر، لوگ) تم سے قیامت کے بارے میں پوچھتے ہیں کہ اس کا وقوع کب ہو گا؟"
  },
  {
    "surah_number": 79,
    "verse_number": 43,
    "content": "سو تم اس کے ذکر سے کس فکر میں ہو"
  },
  {
    "surah_number": 79,
    "verse_number": 44,
    "content":
        "اس کا منتہا (یعنی واقع ہونے کا وقت) تمہارے پروردگار ہی کو (معلوم ہے)"
  },
  {
    "surah_number": 79,
    "verse_number": 45,
    "content": "جو شخص اس سے ڈر رکھتا ہے تم تو اسی کو ڈر سنانے والے ہو"
  },
  {
    "surah_number": 79,
    "verse_number": 46,
    "content":
        "جب وہ اس کو دیکھیں گے (تو ایسا خیال کریں گے) کہ گویا (دنیا میں صرف) ایک شام یا صبح رہے تھے"
  },
  {
    "surah_number": 80,
    "verse_number": 1,
    "content": "(محمد مصطفٰےﷺ) ترش رُو ہوئے اور منہ پھیر بیٹھے"
  },
  {
    "surah_number": 80,
    "verse_number": 2,
    "content": "کہ ان کے پاس ایک نابینا آیا"
  },
  {
    "surah_number": 80,
    "verse_number": 3,
    "content": "اور تم کو کیا خبر شاید وہ پاکیزگی حاصل کرتا"
  },
  {
    "surah_number": 80,
    "verse_number": 4,
    "content": "یا سوچتا تو سمجھانا اسے فائدہ دیتا"
  },
  {"surah_number": 80, "verse_number": 5, "content": "جو پروا نہیں کرتا"},
  {
    "surah_number": 80,
    "verse_number": 6,
    "content": "اس کی طرف تو تم توجہ کرتے ہو"
  },
  {
    "surah_number": 80,
    "verse_number": 7,
    "content": "حالانکہ اگر وہ نہ سنورے تو تم پر کچھ (الزام) نہیں"
  },
  {
    "surah_number": 80,
    "verse_number": 8,
    "content": "اور جو تمہارے پاس دوڑتا ہوا آیا"
  },
  {"surah_number": 80, "verse_number": 9, "content": "اور (خدا سے) ڈرتا ہے"},
  {"surah_number": 80, "verse_number": 10, "content": "اس سے تم بےرخی کرتے ہو"},
  {
    "surah_number": 80,
    "verse_number": 11,
    "content": "دیکھو یہ (قرآن) نصیحت ہے"
  },
  {
    "surah_number": 80,
    "verse_number": 12,
    "content": "پس جو چاہے اسے یاد رکھے"
  },
  {
    "surah_number": 80,
    "verse_number": 13,
    "content": "قابل ادب ورقوں میں (لکھا ہوا)"
  },
  {
    "surah_number": 80,
    "verse_number": 14,
    "content": "جو بلند مقام پر رکھے ہوئے (اور) پاک ہیں"
  },
  {
    "surah_number": 80,
    "verse_number": 15,
    "content": "لکھنے والوں کے ہاتھوں میں"
  },
  {
    "surah_number": 80,
    "verse_number": 16,
    "content": "جو سردار اور نیکو کار ہیں"
  },
  {
    "surah_number": 80,
    "verse_number": 17,
    "content": "انسان ہلاک ہو جائے کیسا ناشکرا ہے"
  },
  {
    "surah_number": 80,
    "verse_number": 18,
    "content": "اُسے (خدا نے) کس چیز سے بنایا؟"
  },
  {
    "surah_number": 80,
    "verse_number": 19,
    "content": "نطفے سے بنایا پھر اس کا اندازہ مقرر کیا"
  },
  {
    "surah_number": 80,
    "verse_number": 20,
    "content": "پھر اس کے لیے رستہ آسان کر دیا"
  },
  {
    "surah_number": 80,
    "verse_number": 21,
    "content": "پھر اس کو موت دی پھر قبر میں دفن کرایا"
  },
  {
    "surah_number": 80,
    "verse_number": 22,
    "content": "پھر جب چاہے گا اسے اٹھا کھڑا کرے گا"
  },
  {
    "surah_number": 80,
    "verse_number": 23,
    "content": "کچھ شک نہیں کہ خدا نے اسے جو حکم دیا اس نے اس پر عمل نہ کیا"
  },
  {
    "surah_number": 80,
    "verse_number": 24,
    "content": "تو انسان کو چاہیئے کہ اپنے کھانے کی طرف نظر کرے"
  },
  {
    "surah_number": 80,
    "verse_number": 25,
    "content": "بے شک ہم ہی نے پانی برسایا"
  },
  {
    "surah_number": 80,
    "verse_number": 26,
    "content": "پھر ہم ہی نے زمین کو چیرا پھاڑا"
  },
  {
    "surah_number": 80,
    "verse_number": 27,
    "content": "پھر ہم ہی نے اس میں اناج اگایا"
  },
  {"surah_number": 80, "verse_number": 28, "content": "اور انگور اور ترکاری"},
  {"surah_number": 80, "verse_number": 29, "content": "اور زیتون اور کھجوریں"},
  {"surah_number": 80, "verse_number": 30, "content": "اور گھنے گھنے باغ"},
  {"surah_number": 80, "verse_number": 31, "content": "اور میوے اور چارا"},
  {
    "surah_number": 80,
    "verse_number": 32,
    "content": "(یہ سب کچھ) تمہارے اور تمہارے چارپایوں کے لیے بنایا"
  },
  {
    "surah_number": 80,
    "verse_number": 33,
    "content": "تو جب (قیامت کا) غل مچے گا"
  },
  {
    "surah_number": 80,
    "verse_number": 34,
    "content": "اس دن آدمی اپنے بھائی سے دور بھاگے گا"
  },
  {
    "surah_number": 80,
    "verse_number": 35,
    "content": "اور اپنی ماں اور اپنے باپ سے"
  },
  {
    "surah_number": 80,
    "verse_number": 36,
    "content": "اور اپنی بیوی اور اپنے بیٹے سے"
  },
  {
    "surah_number": 80,
    "verse_number": 37,
    "content":
        "ہر شخص اس روز ایک فکر میں ہو گا جو اسے (مصروفیت کے لیے) بس کرے گا"
  },
  {
    "surah_number": 80,
    "verse_number": 38,
    "content": "اور کتنے منہ اس روز چمک رہے ہوں گے"
  },
  {
    "surah_number": 80,
    "verse_number": 39,
    "content": "خنداں و شاداں (یہ مومنان نیکو کار ہیں)"
  },
  {
    "surah_number": 80,
    "verse_number": 40,
    "content": "اور کتنے منہ ہوں گے جن پر گرد پڑ رہی ہو گی"
  },
  {
    "surah_number": 80,
    "verse_number": 41,
    "content": "(اور) سیاہی چڑھ رہی ہو گی"
  },
  {"surah_number": 80, "verse_number": 42, "content": "یہ کفار بدکردار ہیں"},
  {
    "surah_number": 81,
    "verse_number": 1,
    "content": "جب سورج لپیٹ لیا جائے گا"
  },
  {
    "surah_number": 81,
    "verse_number": 2,
    "content": "جب تارے بےنور ہو جائیں گے"
  },
  {
    "surah_number": 81,
    "verse_number": 3,
    "content": "اور جب پہاڑ چلائے جائیں گے"
  },
  {
    "surah_number": 81,
    "verse_number": 4,
    "content": "اور جب بیانے والی اونٹنیاں بےکار ہو جائیں گی"
  },
  {
    "surah_number": 81,
    "verse_number": 5,
    "content": "اور جب وحشی جانور جمع اکٹھے ہو جائیں گے"
  },
  {
    "surah_number": 81,
    "verse_number": 6,
    "content": "اور جب دریا آگ ہو جائیں گے"
  },
  {
    "surah_number": 81,
    "verse_number": 7,
    "content": "اور جب روحیں (بدنوں سے) ملا دی جائیں گی"
  },
  {
    "surah_number": 81,
    "verse_number": 8,
    "content": "اور جب لڑکی سے جو زندہ دفنا دی گئی ہو پوچھا جائے گا"
  },
  {
    "surah_number": 81,
    "verse_number": 9,
    "content": "کہ وہ کس گناہ پرماری گئی"
  },
  {
    "surah_number": 81,
    "verse_number": 10,
    "content": "اور جب (عملوں کے) دفتر کھولے جائیں گے"
  },
  {
    "surah_number": 81,
    "verse_number": 11,
    "content": "اور جب آسمانوں کی کھال کھینچ لی جائے گی"
  },
  {
    "surah_number": 81,
    "verse_number": 12,
    "content": "اور جب دوزخ (کی آگ) بھڑکائی جائے گی"
  },
  {
    "surah_number": 81,
    "verse_number": 13,
    "content": "اور بہشت جب قریب لائی جائے گی"
  },
  {
    "surah_number": 81,
    "verse_number": 14,
    "content": "تب ہر شخص معلوم کر لے گا کہ وہ کیا لے کر آیا ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 15,
    "content": "ہم کو ان ستاروں کی قسم جو پیچھے ہٹ جاتے ہیں"
  },
  {
    "surah_number": 81,
    "verse_number": 16,
    "content": "(اور) جو سیر کرتے اور غائب ہو جاتے ہیں"
  },
  {
    "surah_number": 81,
    "verse_number": 17,
    "content": "اور رات کی قسم جب ختم ہونے لگتی ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 18,
    "content": "اور صبح کی قسم جب نمودار ہوتی ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 19,
    "content": "کہ بےشک یہ (قرآن) فرشتہٴ عالی مقام کی زبان کا پیغام ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 20,
    "content": "جو صاحب قوت مالک عرش کے ہاں اونچے درجے والا ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 21,
    "content": "سردار (اور) امانت دار ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 22,
    "content": "اور (مکے والو) تمہارے رفیق (یعنی محمدﷺ) دیوانے نہیں ہیں"
  },
  {
    "surah_number": 81,
    "verse_number": 23,
    "content":
        "بےشک انہوں نے اس (فرشتے) کو (آسمان کے کھلے یعنی) مشرقی کنارے پر دیکھا ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 24,
    "content": "اور وہ پوشیدہ باتوں (کے ظاہر کرنے) میں بخیل نہیں"
  },
  {
    "surah_number": 81,
    "verse_number": 25,
    "content": "اور یہ شیطان مردود کا کلام نہیں"
  },
  {"surah_number": 81, "verse_number": 26, "content": "پھر تم کدھر جا رہے ہو"},
  {
    "surah_number": 81,
    "verse_number": 27,
    "content": "یہ تو جہان کے لوگوں کے لیے نصیحت ہے"
  },
  {
    "surah_number": 81,
    "verse_number": 28,
    "content": "(یعنی) اس کے لیے جو تم میں سے سیدھی چال چلنا چاہے"
  },
  {
    "surah_number": 81,
    "verse_number": 29,
    "content": "اور تم کچھ بھی نہیں چاہ سکتے مگر وہی جو خدائے رب العالمین چاہے"
  },
  {"surah_number": 82, "verse_number": 1, "content": "جب آسمان پھٹ جائے گا"},
  {"surah_number": 82, "verse_number": 2, "content": "اور جب تارے جھڑ پڑیں گے"},
  {
    "surah_number": 82,
    "verse_number": 3,
    "content": "اور جب دریا بہہ (کر ایک دوسرے سے مل) جائیں گے"
  },
  {
    "surah_number": 82,
    "verse_number": 4,
    "content": "اور جب قبریں اکھیڑ دی جائیں گی"
  },
  {
    "surah_number": 82,
    "verse_number": 5,
    "content":
        "تب ہر شخص معلوم کرلے گا کہ اس نے آگے کیا بھیجا تھا اور پیچھے کیا چھوڑا تھا"
  },
  {
    "surah_number": 82,
    "verse_number": 6,
    "content":
        "اے انسان تجھ کو اپنے پروردگار کرم گستر کے باب میں کس چیز نے دھوکا دیا"
  },
  {
    "surah_number": 82,
    "verse_number": 7,
    "content":
        "(وہی تو ہے) جس نے تجھے بنایا اور (تیرے اعضا کو) ٹھیک کیا اور (تیرے قامت کو) معتدل رکھا"
  },
  {
    "surah_number": 82,
    "verse_number": 8,
    "content": "اور جس صورت میں چاہا تجھے جوڑ دیا"
  },
  {
    "surah_number": 82,
    "verse_number": 9,
    "content": "مگر ہیہات تم لوگ جزا کو جھٹلاتے ہو"
  },
  {
    "surah_number": 82,
    "verse_number": 10,
    "content": "حالانکہ تم پر نگہبان مقرر ہیں"
  },
  {
    "surah_number": 82,
    "verse_number": 11,
    "content": "عالی قدر (تمہاری باتوں کے) لکھنے والے"
  },
  {
    "surah_number": 82,
    "verse_number": 12,
    "content": "جو تم کرتے ہو وہ اسے جانتے ہیں"
  },
  {
    "surah_number": 82,
    "verse_number": 13,
    "content": "بے شک نیکوکار نعمتوں (کی بہشت) میں ہوں گے۔"
  },
  {"surah_number": 82, "verse_number": 14, "content": "اور بدکردار دوزخ میں"},
  {
    "surah_number": 82,
    "verse_number": 15,
    "content": "(یعنی) جزا کے دن اس میں داخل ہوں گے"
  },
  {
    "surah_number": 82,
    "verse_number": 16,
    "content": "اور اس سے چھپ نہیں سکیں گے"
  },
  {
    "surah_number": 82,
    "verse_number": 17,
    "content": "اور تمہیں کیا معلوم کہ جزا کا دن کیسا ہے؟"
  },
  {
    "surah_number": 82,
    "verse_number": 18,
    "content": "پھر تمہیں کیا معلوم کہ جزا کا دن کیسا ہے؟"
  },
  {
    "surah_number": 82,
    "verse_number": 19,
    "content":
        "جس روز کوئی کسی کا بھلا نہ کر سکے گا اور حکم اس روز خدا ہی کا ہو گا"
  },
  {
    "surah_number": 83,
    "verse_number": 1,
    "content": "ناپ اور تول میں کمی کرنے والوں کے لیے خرابی ہے"
  },
  {
    "surah_number": 83,
    "verse_number": 2,
    "content": "جو لوگوں سے ناپ کر لیں تو پورا لیں"
  },
  {
    "surah_number": 83,
    "verse_number": 3,
    "content": "اور جب ان کو ناپ کر یا تول کر دیں تو کم کر دیں"
  },
  {
    "surah_number": 83,
    "verse_number": 4,
    "content": "کیا یہ لوگ نہیں جانتے کہ اٹھائے بھی جائیں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 5,
    "content": "(یعنی) ایک بڑے (سخت) دن میں"
  },
  {
    "surah_number": 83,
    "verse_number": 6,
    "content": "جس دن (تمام) لوگ رب العالمین کے سامنے کھڑے ہوں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 7,
    "content": "سن رکھو کہ بدکارروں کے اعمال سجّین میں ہیں"
  },
  {
    "surah_number": 83,
    "verse_number": 8,
    "content": "اور تم کیا جانتے ہوں کہ سجّین کیا چیز ہے؟"
  },
  {"surah_number": 83, "verse_number": 9, "content": "ایک دفتر ہے لکھا ہوا"},
  {
    "surah_number": 83,
    "verse_number": 10,
    "content": "اس دن جھٹلانے والوں کی خرابی ہے"
  },
  {
    "surah_number": 83,
    "verse_number": 11,
    "content": "(یعنی) جو انصاف کے دن کو جھٹلاتے ہیں"
  },
  {
    "surah_number": 83,
    "verse_number": 12,
    "content": "اور اس کو جھٹلاتا وہی ہے جو حد سے نکل جانے والا گنہگار ہے"
  },
  {
    "surah_number": 83,
    "verse_number": 13,
    "content":
        "جب اس کو ہماری آیتیں سنائی جاتی ہیں تو کہتا ہے کہ یہ تو اگلے لوگوں کے افسانے ہیں"
  },
  {
    "surah_number": 83,
    "verse_number": 14,
    "content":
        "دیکھو یہ جو (اعمال بد) کرتے ہیں ان کا ان کے دلوں پر زنگ بیٹھ گیا ہے"
  },
  {
    "surah_number": 83,
    "verse_number": 15,
    "content": "بےشک یہ لوگ اس روز اپنے پروردگار (کے دیدار) سے اوٹ میں ہوں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 16,
    "content": "پھر دوزخ میں جا داخل ہوں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 17,
    "content": "پھر ان سے کہا جائے گا کہ یہ وہی چیز ہے جس کو تم جھٹلاتے تھے"
  },
  {
    "surah_number": 83,
    "verse_number": 18,
    "content": "(یہ بھی) سن رکھو کہ نیکوکاروں کے اعمال علیین میں ہیں"
  },
  {
    "surah_number": 83,
    "verse_number": 19,
    "content": "اور تم کو کیا معلوم کہ علیین کیا چیز ہے؟"
  },
  {"surah_number": 83, "verse_number": 20, "content": "ایک دفتر ہے لکھا ہوا"},
  {
    "surah_number": 83,
    "verse_number": 21,
    "content": "جس کے پاس مقرب (فرشتے) حاضر رہتے ہیں"
  },
  {
    "surah_number": 83,
    "verse_number": 22,
    "content": "بےشک نیک لوگ چین میں ہوں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 23,
    "content": "تختوں پر بیٹھے ہوئے نظارے کریں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 24,
    "content": "تم ان کے چہروں ہی سے راحت کی تازگی معلوم کر لو گے"
  },
  {
    "surah_number": 83,
    "verse_number": 25,
    "content": "ان کو خالص شراب سربمہر پلائی جائے گی"
  },
  {
    "surah_number": 83,
    "verse_number": 26,
    "content":
        "جس کی مہر مشک کی ہو گی تو (نعمتوں کے) شائقین کو چاہیے کہ اسی سے رغبت کریں"
  },
  {
    "surah_number": 83,
    "verse_number": 27,
    "content": "اور اس میں تسنیم (کے پانی) کی آمیزش ہو گی"
  },
  {
    "surah_number": 83,
    "verse_number": 28,
    "content": "وہ ایک چشمہ ہے جس میں سے (خدا کے) مقرب پیئیں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 29,
    "content":
        "جو گنہگار (یعنی کفار) ہیں وہ (دنیا میں) مومنوں سے ہنسی کیا کرتے تھے"
  },
  {
    "surah_number": 83,
    "verse_number": 30,
    "content": "اور جب ان کے پاس سے گزرتے تو حقارت سے اشارے کرتے"
  },
  {
    "surah_number": 83,
    "verse_number": 31,
    "content": "اور جب اپنے گھر کو لوٹتے تو اتراتے ہوئے لوٹتے"
  },
  {
    "surah_number": 83,
    "verse_number": 32,
    "content": "اور جب ان (مومنوں) کو دیکھتے تو کہتے کہ یہ تو گمراہ ہیں"
  },
  {
    "surah_number": 83,
    "verse_number": 33,
    "content": "حالانکہ وہ ان پر نگراں بنا کر نہیں بھیجے گئے تھے"
  },
  {
    "surah_number": 83,
    "verse_number": 34,
    "content": "تو آج مومن کافروں سے ہنسی کریں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 35,
    "content": "(اور) تختوں پر (بیٹھے ہوئے ان کا حال) دیکھ رہے ہوں گے"
  },
  {
    "surah_number": 83,
    "verse_number": 36,
    "content": "تو کافروں کو ان کے عملوں کا (پورا پورا) بدلہ مل گیا"
  },
  {"surah_number": 84, "verse_number": 1, "content": "جب آسمان پھٹ جائے گا"},
  {
    "surah_number": 84,
    "verse_number": 2,
    "content":
        "اور اپنے پروردگار کا فرمان بجا لائے گا اور اسے واجب بھی یہ ہی ہے"
  },
  {
    "surah_number": 84,
    "verse_number": 3,
    "content": "اور جب زمین ہموار کر دی جائے گی"
  },
  {
    "surah_number": 84,
    "verse_number": 4,
    "content":
        "جو کچھ اس میں ہے اسے نکال کر باہر ڈال دے گی اور (بالکل) خالی ہو جائے گی"
  },
  {
    "surah_number": 84,
    "verse_number": 5,
    "content":
        "اور اپنے پروردگار کے ارشاد کی تعمیل کرے گی اور اس کو لازم بھی یہی ہے (تو قیامت قائم ہو جائے گی)"
  },
  {
    "surah_number": 84,
    "verse_number": 6,
    "content":
        "اے انسان! تو اپنے پروردگار کی طرف (پہنچنے میں) خوب کوشِش کرتا ہے سو اس سے جا ملے گا"
  },
  {
    "surah_number": 84,
    "verse_number": 7,
    "content": "تو جس کا نامہٴ (اعمال) اس کے داہنے ہاتھ میں دیا جائے گا"
  },
  {
    "surah_number": 84,
    "verse_number": 8,
    "content": "اس سے حساب آسان لیا جائے گا"
  },
  {
    "surah_number": 84,
    "verse_number": 9,
    "content": "اور وہ اپنے گھر والوں میں خوش خوش آئے گا"
  },
  {
    "surah_number": 84,
    "verse_number": 10,
    "content": "اور جس کا نامہٴ (اعمال) اس کی پیٹھ کے پیچھے سے دیا جائے گا"
  },
  {"surah_number": 84, "verse_number": 11, "content": "وہ موت کو پکارے گا"},
  {
    "surah_number": 84,
    "verse_number": 12,
    "content": "اور وہ دوزخ میں داخل ہو گا"
  },
  {
    "surah_number": 84,
    "verse_number": 13,
    "content": "یہ اپنے اہل (و عیال) میں مست رہتا تھا"
  },
  {
    "surah_number": 84,
    "verse_number": 14,
    "content": "اور خیال کرتا تھا کہ (خدا کی طرف) پھر کر نہ جائے گا"
  },
  {
    "surah_number": 84,
    "verse_number": 15,
    "content": "ہاں ہاں۔ اس کا پروردگار اس کو دیکھ رہا تھا"
  },
  {
    "surah_number": 84,
    "verse_number": 16,
    "content": "ہمیں شام کی سرخی کی قسم"
  },
  {
    "surah_number": 84,
    "verse_number": 17,
    "content": "اور رات کی اور جن چیزوں کو وہ اکٹھا کر لیتی ہے ان کی"
  },
  {
    "surah_number": 84,
    "verse_number": 18,
    "content": "اور چاند کی جب کامل ہو جائے"
  },
  {
    "surah_number": 84,
    "verse_number": 19,
    "content": "کہ تم درجہ بدرجہ (رتبہٴ اعلیٰ پر) چڑھو گے"
  },
  {
    "surah_number": 84,
    "verse_number": 20,
    "content": "تو ان لوگوں کو کیا ہوا ہے کہ ایمان نہیں لاتے"
  },
  {
    "surah_number": 84,
    "verse_number": 21,
    "content": "اور جب ان کے سامنے قرآن پڑھا جاتا ہے تو سجدہ نہیں کرتے"
  },
  {"surah_number": 84, "verse_number": 22, "content": "بلکہ کافر جھٹلاتے ہیں"},
  {
    "surah_number": 84,
    "verse_number": 23,
    "content": "اور خدا ان باتوں کو جو یہ اپنے دلوں میں چھپاتے ہیں خوب جانتا ہے"
  },
  {
    "surah_number": 84,
    "verse_number": 24,
    "content": "تو ان کو دکھ دینے والے عذاب کی خبر سنا دو"
  },
  {
    "surah_number": 84,
    "verse_number": 25,
    "content":
        "ہاں جو لوگ ایمان لائے اور نیک عمل کرتے رہے ان کے لیے بےانتہا اجر ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 1,
    "content": "آسمان کی قسم جس میں برج ہیں"
  },
  {
    "surah_number": 85,
    "verse_number": 2,
    "content": "اور اس دن کی جس کا وعدہ ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 3,
    "content": "اور حاضر ہونے والے کی اور جو اس کے پاس حاضر کیا جائے اسکی"
  },
  {
    "surah_number": 85,
    "verse_number": 4,
    "content": "کہ خندقوں (کے کھودنے) والے ہلاک کر دیئے گئے"
  },
  {
    "surah_number": 85,
    "verse_number": 5,
    "content": "(یعنی) آگ (کی خندقیں) جس میں ایندھن (جھونک رکھا) تھا"
  },
  {
    "surah_number": 85,
    "verse_number": 6,
    "content": "جب کہ وہ ان (کے کناروں) پر بیٹھے ہوئے تھے"
  },
  {
    "surah_number": 85,
    "verse_number": 7,
    "content":
        "اور جو (سختیاں) اہل ایمان پر کر رہے تھے ان کو سامنے دیکھ رہے تھے"
  },
  {
    "surah_number": 85,
    "verse_number": 8,
    "content":
        "ان کو مومنوں کی یہی بات بری لگتی تھی کہ وہ خدا پر ایمان لائے ہوئے تھے جو غالب (اور) قابل ستائش ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 9,
    "content":
        "وہی جس کی آسمانوں اور زمین میں بادشاہت ہے۔ اور خدا ہر چیز سے واقف ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 10,
    "content":
        "جن لوگوں نے مومن مردوں اور مومن عورتوں کو تکلیفیں دیں اور توبہ نہ کی ان کو دوزخ کا (اور) عذاب بھی ہوگا اور جلنے کا عذاب بھی ہوگا"
  },
  {
    "surah_number": 85,
    "verse_number": 11,
    "content":
        "(اور) جو لوگ ایمان لائے اور نیک کام کرتے رہے ان کے لیے باغات ہیں جن کے نیچے نہریں بہہ رہی ہیں۔ یہ ہی بڑی کامیابی ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 12,
    "content": "بےشک تمہارے پروردگار کی پکڑ بڑی سخت ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 13,
    "content": "وہی پہلی دفعہ پیدا کرتا ہے اور وہی دوبارہ (زندہ) کرے گا"
  },
  {
    "surah_number": 85,
    "verse_number": 14,
    "content": "اور وہ بخشنے والا اور محبت کرنے والا ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 15,
    "content": "عرش کا مالک بڑی شان والا"
  },
  {"surah_number": 85, "verse_number": 16, "content": "جو چاہتا ہے کر دیتا ہے"},
  {
    "surah_number": 85,
    "verse_number": 17,
    "content": "بھلا تم کو لشکروں کا حال معلوم ہوا ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 18,
    "content": "(یعنی) فرعون اور ثمود کا"
  },
  {
    "surah_number": 85,
    "verse_number": 19,
    "content": "لیکن کافر (جان بوجھ کر) تکذیب میں (گرفتار) ہیں"
  },
  {
    "surah_number": 85,
    "verse_number": 20,
    "content": "اور خدا (بھی) ان کو گردا گرد سے گھیرے ہوئے ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 21,
    "content": "(یہ کتاب ہزل و بطلان نہیں) بلکہ یہ قرآن عظیم الشان ہے"
  },
  {
    "surah_number": 85,
    "verse_number": 22,
    "content": "لوح محفوظ میں (لکھا ہوا)"
  },
  {
    "surah_number": 86,
    "verse_number": 1,
    "content": "آسمان اور رات کے وقت آنے والے کی قسم"
  },
  {
    "surah_number": 86,
    "verse_number": 2,
    "content": "اور تم کو کیا معلوم کہ رات کے وقت آنے والا کیا ہے"
  },
  {"surah_number": 86, "verse_number": 3, "content": "وہ تارا ہے چمکنے والا"},
  {
    "surah_number": 86,
    "verse_number": 4,
    "content": "کہ کوئی متنفس نہیں جس پر نگہبان مقرر نہیں"
  },
  {
    "surah_number": 86,
    "verse_number": 5,
    "content": "تو انسان کو دیکھنا چاہئے کہ وہ کاہے سے پیدا ہوا ہے"
  },
  {
    "surah_number": 86,
    "verse_number": 6,
    "content": "وہ اچھلتے ہوئے پانی سے پیدا ہوا ہے"
  },
  {
    "surah_number": 86,
    "verse_number": 7,
    "content": "جو پیٹھ اور سینے کے بیچ میں سے نکلتا ہے"
  },
  {
    "surah_number": 86,
    "verse_number": 8,
    "content": "بےشک خدا اس کے اعادے (یعنی پھر پیدا کرنے) پر قادر ہے"
  },
  {
    "surah_number": 86,
    "verse_number": 9,
    "content": "جس دن دلوں کے بھید جانچے جائیں گے"
  },
  {
    "surah_number": 86,
    "verse_number": 10,
    "content": "تو انسان کی کچھ پیش نہ چل سکے گی اور نہ کوئی اس کا مددگار ہو گا"
  },
  {
    "surah_number": 86,
    "verse_number": 11,
    "content": "آسمان کی قسم جو مینہ برساتا ہے"
  },
  {
    "surah_number": 86,
    "verse_number": 12,
    "content": "اور زمین کی قسم جو پھٹ جاتی ہے"
  },
  {
    "surah_number": 86,
    "verse_number": 13,
    "content": "کہ یہ کلام (حق کو باطل سے) جدا کرنے والا ہے"
  },
  {"surah_number": 86, "verse_number": 14, "content": "اور بیہودہ بات نہیں ہے"},
  {
    "surah_number": 86,
    "verse_number": 15,
    "content": "یہ لوگ تو اپنی تدبیروں میں لگ رہے ہیں"
  },
  {
    "surah_number": 86,
    "verse_number": 16,
    "content": "اور ہم اپنی تدبیر کر رہے ہیں"
  },
  {
    "surah_number": 86,
    "verse_number": 17,
    "content": "تو تم کافروں کو مہلت دو بس چند روز ہی مہلت دو"
  },
  {
    "surah_number": 87,
    "verse_number": 1,
    "content": "(اے پیغمبر) اپنے پروردگار جلیل الشان کے نام کی تسبیح کرو"
  },
  {
    "surah_number": 87,
    "verse_number": 2,
    "content": "جس نے (انسان کو) بنایا پھر (اس کے اعضاء کو) درست کیا"
  },
  {
    "surah_number": 87,
    "verse_number": 3,
    "content": "اور جس نے (اس کا) اندازہ ٹہرایا (پھر اس کو) رستہ بتایا"
  },
  {"surah_number": 87, "verse_number": 4, "content": "اور جس نے چارہ اگایا"},
  {
    "surah_number": 87,
    "verse_number": 5,
    "content": "پھر اس کو سیاہ رنگ کا کوڑا کر دیا"
  },
  {
    "surah_number": 87,
    "verse_number": 6,
    "content": "ہم تمہیں پڑھا دیں گے کہ تم فراموش نہ کرو گے"
  },
  {
    "surah_number": 87,
    "verse_number": 7,
    "content": "مگر جو خدا چاہے۔ وہ کھلی بات کو بھی جانتا ہے اور چھپی کو بھی"
  },
  {
    "surah_number": 87,
    "verse_number": 8,
    "content": "ہم تم کو آسان طریقے کی توفیق دیں گے"
  },
  {
    "surah_number": 87,
    "verse_number": 9,
    "content": "سو جہاں تک نصیحت (کے) نافع (ہونے کی امید) ہو نصیحت کرتے رہو"
  },
  {
    "surah_number": 87,
    "verse_number": 10,
    "content": "جو خوف رکھتا ہے وہ تو نصیحت پکڑے گا"
  },
  {
    "surah_number": 87,
    "verse_number": 11,
    "content": "اور (بےخوف) بدبخت پہلو تہی کرے گا"
  },
  {
    "surah_number": 87,
    "verse_number": 12,
    "content": "جو (قیامت کو) بڑی (تیز) آگ میں داخل ہو گا"
  },
  {
    "surah_number": 87,
    "verse_number": 13,
    "content": "پھر وہاں نہ مرے گا اور نہ جئے گا"
  },
  {
    "surah_number": 87,
    "verse_number": 14,
    "content": "بے شک وہ مراد کو پہنچ گیا جو پاک ہوا"
  },
  {
    "surah_number": 87,
    "verse_number": 15,
    "content": "اور اپنے پروردگار کے نام کا ذکر کرتا رہا اور نماز پڑھتا رہا"
  },
  {
    "surah_number": 87,
    "verse_number": 16,
    "content": "مگر تم لوگ تو دنیا کی زندگی کو اختیار کرتے ہو"
  },
  {
    "surah_number": 87,
    "verse_number": 17,
    "content": "حالانکہ آخرت بہت بہتر اور پائندہ تر ہے"
  },
  {
    "surah_number": 87,
    "verse_number": 18,
    "content": "یہ بات پہلے صحیفوں میں (مرقوم) ہے"
  },
  {
    "surah_number": 87,
    "verse_number": 19,
    "content": "(یعنی) ابراہیم اور موسیٰ کے صحیفوں میں"
  },
  {
    "surah_number": 88,
    "verse_number": 1,
    "content": "بھلا تم کو ڈھانپ لینے والی (یعنی قیامت کا) حال معلوم ہوا ہے"
  },
  {
    "surah_number": 88,
    "verse_number": 2,
    "content": "اس روز بہت سے منہ (والے) ذلیل ہوں گے"
  },
  {
    "surah_number": 88,
    "verse_number": 3,
    "content": "سخت محنت کرنے والے تھکے ماندے"
  },
  {
    "surah_number": 88,
    "verse_number": 4,
    "content": "دہکتی آگ میں داخل ہوں گے"
  },
  {
    "surah_number": 88,
    "verse_number": 5,
    "content": "ایک کھولتے ہوئے چشمے کا ان کو پانی پلایا جائے گا"
  },
  {
    "surah_number": 88,
    "verse_number": 6,
    "content": "اور خار دار جھاڑ کے سوا ان کے لیے کوئی کھانا نہیں (ہو گا)"
  },
  {
    "surah_number": 88,
    "verse_number": 7,
    "content": "جو نہ فربہی لائے اور نہ بھوک میں کچھ کام آئے"
  },
  {
    "surah_number": 88,
    "verse_number": 8,
    "content": "اور بہت سے منہ (والے) اس روز شادماں ہوں گے"
  },
  {
    "surah_number": 88,
    "verse_number": 9,
    "content": "اپنے اعمال (کی جزا) سے خوش دل"
  },
  {"surah_number": 88, "verse_number": 10, "content": "بہشت بریں میں"},
  {
    "surah_number": 88,
    "verse_number": 11,
    "content": "وہاں کسی طرح کی بکواس نہیں سنیں گے"
  },
  {
    "surah_number": 88,
    "verse_number": 12,
    "content": "اس میں چشمے بہ رہے ہوں گے"
  },
  {
    "surah_number": 88,
    "verse_number": 13,
    "content": "وہاں تخت ہوں گے اونچے بچھے ہوئے"
  },
  {
    "surah_number": 88,
    "verse_number": 14,
    "content": "اور آبخورے (قرینے سے) رکھے ہوئے"
  },
  {
    "surah_number": 88,
    "verse_number": 15,
    "content": "اور گاؤ تکیے قطار کی قطار لگے ہوئے"
  },
  {
    "surah_number": 88,
    "verse_number": 16,
    "content": "اور نفیس مسندیں بچھی ہوئی"
  },
  {
    "surah_number": 88,
    "verse_number": 17,
    "content":
        "یہ لوگ اونٹوں کی طرف نہیں دیکھتے کہ کیسے (عجیب) پیدا کیے گئے ہیں"
  },
  {
    "surah_number": 88,
    "verse_number": 18,
    "content": "اور آسمان کی طرف کہ کیسا بلند کیا گیا ہے"
  },
  {
    "surah_number": 88,
    "verse_number": 19,
    "content": "اور پہاڑوں کی طرف کہ کس طرح کھڑے کیے گئے ہیں"
  },
  {
    "surah_number": 88,
    "verse_number": 20,
    "content": "اور زمین کی طرف کہ کس طرح بچھائی گئی"
  },
  {
    "surah_number": 88,
    "verse_number": 21,
    "content": "تو تم نصیحت کرتے رہو کہ تم نصیحت کرنے والے ہی ہو"
  },
  {
    "surah_number": 88,
    "verse_number": 22,
    "content": "تم ان پر داروغہ نہیں ہو"
  },
  {
    "surah_number": 88,
    "verse_number": 23,
    "content": "ہاں جس نے منہ پھیرا اور نہ مانا"
  },
  {
    "surah_number": 88,
    "verse_number": 24,
    "content": "تو خدا اس کو بڑا عذاب دے گا"
  },
  {
    "surah_number": 88,
    "verse_number": 25,
    "content": "بےشک ان کو ہمارے پاس لوٹ کر آنا ہے"
  },
  {
    "surah_number": 88,
    "verse_number": 26,
    "content": "پھر ہم ہی کو ان سے حساب لینا ہے"
  },
  {"surah_number": 89, "verse_number": 1, "content": "فجر کی قسم"},
  {"surah_number": 89, "verse_number": 2, "content": "اور دس راتوں کی"},
  {"surah_number": 89, "verse_number": 3, "content": "اور جفت اور طاق کی"},
  {"surah_number": 89, "verse_number": 4, "content": "اور رات کی جب جانے لگے"},
  {
    "surah_number": 89,
    "verse_number": 5,
    "content":
        "اور بے شک یہ چیزیں عقلمندوں کے نزدیک قسم کھانے کے لائق ہیں کہ (کافروں کو ضرور عذاب ہو گا)"
  },
  {
    "surah_number": 89,
    "verse_number": 6,
    "content": "کیا تم نے نہیں دیکھا کہ تمہارے پروردگار نے عاد کے ساتھ کیا کیا"
  },
  {
    "surah_number": 89,
    "verse_number": 7,
    "content": "(جو) ارم (کہلاتے تھے اتنے) دراز قد"
  },
  {
    "surah_number": 89,
    "verse_number": 8,
    "content": "کہ تمام ملک میں ایسے پیدا نہیں ہوئے تھے"
  },
  {
    "surah_number": 89,
    "verse_number": 9,
    "content":
        "اور ثمود کے ساتھ (کیا کیا) جو وادئِ (قریٰ) میں پتھر تراشتے تھے (اور گھر بناتے) تھے"
  },
  {
    "surah_number": 89,
    "verse_number": 10,
    "content": "اور فرعون کے ساتھ (کیا کیا) جو خیمے اور میخیں رکھتا تھا"
  },
  {
    "surah_number": 89,
    "verse_number": 11,
    "content": "یہ لوگ ملکوں میں سرکش ہو رہے تھے"
  },
  {
    "surah_number": 89,
    "verse_number": 12,
    "content": "اور ان میں بہت سی خرابیاں کرتے تھے"
  },
  {
    "surah_number": 89,
    "verse_number": 13,
    "content": "تو تمہارے پروردگار نے ان پر عذاب کا کوڑا نازل کیا"
  },
  {
    "surah_number": 89,
    "verse_number": 14,
    "content": "بے شک تمہارا پروردگار تاک میں ہے"
  },
  {
    "surah_number": 89,
    "verse_number": 15,
    "content":
        "مگر انسان (عجیب مخلوق ہے کہ) جب اس کا پروردگار اس کو آزماتا ہے تو اسے عزت دیتا اور نعمت بخشتا ہے۔ تو کہتا ہے کہ (آہا) میرے پروردگار نے مجھے عزت بخشی"
  },
  {
    "surah_number": 89,
    "verse_number": 16,
    "content":
        "اور جب (دوسری طرح) آزماتا ہے کہ اس پر روزی تنگ کر دیتا ہے تو کہتا ہے کہ (ہائے) میرے پروردگار نے مجھے ذلیل کیا"
  },
  {
    "surah_number": 89,
    "verse_number": 17,
    "content": "نہیں بلکہ تم لوگ یتیم کی خاطر نہیں کرتے"
  },
  {
    "surah_number": 89,
    "verse_number": 18,
    "content": "اور نہ مسکین کو کھانا کھلانے کی ترغیب دیتے ہو"
  },
  {
    "surah_number": 89,
    "verse_number": 19,
    "content": "اور میراث کے مال سمیٹ کر کھا جاتے ہو"
  },
  {
    "surah_number": 89,
    "verse_number": 20,
    "content": "اور مال کو بہت ہی عزیز رکھتے ہو"
  },
  {
    "surah_number": 89,
    "verse_number": 21,
    "content": "تو جب زمین کی بلندی کوٹ کوٹ کو پست کر دی جائے گی"
  },
  {
    "surah_number": 89,
    "verse_number": 22,
    "content":
        "اور تمہارا پروردگار (جلوہ فرما ہو گا) اور فرشتے قطار باندھ باندھ کر آ موجود ہوں گے"
  },
  {
    "surah_number": 89,
    "verse_number": 23,
    "content":
        "اور دوزخ اس دن حاضر کی جائے گی تو انسان اس دن متنبہ ہو گا مگر تنبہ (سے) اسے (فائدہ) کہاں (مل سکے گا)"
  },
  {
    "surah_number": 89,
    "verse_number": 24,
    "content":
        "کہے گا کاش میں نے اپنی زندگی (جاودانی کے لیے) کچھ آگے بھیجا ہوتا"
  },
  {
    "surah_number": 89,
    "verse_number": 25,
    "content": "تو اس دن نہ کوئی خدا کے عذاب کی طرح کا (کسی کو) عذاب دے گا"
  },
  {
    "surah_number": 89,
    "verse_number": 26,
    "content": "اور نہ کوئی ویسا جکڑنا جکڑے گا"
  },
  {
    "surah_number": 89,
    "verse_number": 27,
    "content": "اے اطمینان پانے والی روح!"
  },
  {
    "surah_number": 89,
    "verse_number": 28,
    "content": "اپنے پروردگار کی طرف لوٹ چل۔ تو اس سے راضی وہ تجھ سے راضی"
  },
  {
    "surah_number": 89,
    "verse_number": 29,
    "content": "تو میرے (ممتاز) بندوں میں شامل ہو جا"
  },
  {
    "surah_number": 89,
    "verse_number": 30,
    "content": "اور میری بہشت میں داخل ہو جا"
  },
  {
    "surah_number": 90,
    "verse_number": 1,
    "content": "ہمیں اس شہر (مکہ) کی قسم"
  },
  {
    "surah_number": 90,
    "verse_number": 2,
    "content": "اور تم اسی شہر میں تو رہتے ہو"
  },
  {
    "surah_number": 90,
    "verse_number": 3,
    "content": "اور باپ (یعنی آدم) اور اس کی اولاد کی قسم"
  },
  {
    "surah_number": 90,
    "verse_number": 4,
    "content": "کہ ہم نے انسان کو تکلیف (کی حالت) میں (رہنے والا) بنایا ہے"
  },
  {
    "surah_number": 90,
    "verse_number": 5,
    "content": "کیا وہ خیال رکھتا ہے کہ اس پر کوئی قابو نہ پائے گا"
  },
  {
    "surah_number": 90,
    "verse_number": 6,
    "content": "کہتا ہے کہ میں نے بہت سا مال برباد کیا"
  },
  {
    "surah_number": 90,
    "verse_number": 7,
    "content": "کیا اسے یہ گمان ہے کہ اس کو کسی نے دیکھا نہیں"
  },
  {
    "surah_number": 90,
    "verse_number": 8,
    "content": "بھلا ہم نےاس کو دو آنکھیں نہیں دیں؟"
  },
  {
    "surah_number": 90,
    "verse_number": 9,
    "content": "اور زبان اور دو ہونٹ (نہیں دیئے)"
  },
  {
    "surah_number": 90,
    "verse_number": 10,
    "content":
        "(یہ چیزیں بھی دیں) اور اس کو (خیر و شر کے) دونوں رستے بھی دکھا دیئے"
  },
  {
    "surah_number": 90,
    "verse_number": 11,
    "content": "مگر وہ گھاٹی پر سے ہو کر نہ گزرا"
  },
  {
    "surah_number": 90,
    "verse_number": 12,
    "content": "اور تم کیا سمجھے کہ گھاٹی کیا ہے؟"
  },
  {
    "surah_number": 90,
    "verse_number": 13,
    "content": "کسی (کی) گردن کا چھڑانا"
  },
  {
    "surah_number": 90,
    "verse_number": 14,
    "content": "یا بھوک کے دن کھانا کھلانا"
  },
  {"surah_number": 90, "verse_number": 15, "content": "یتیم رشتہ دار کو"},
  {"surah_number": 90, "verse_number": 16, "content": "یا فقیر خاکسار کو"},
  {
    "surah_number": 90,
    "verse_number": 17,
    "content":
        "پھر ان لوگوں میں بھی (داخل) ہو جو ایمان لائے اور صبر کی نصیحت اور (لوگوں پر) شفقت کرنے کی وصیت کرتے رہے"
  },
  {"surah_number": 90, "verse_number": 18, "content": "یہی لوگ صاحب سعادت ہیں"},
  {
    "surah_number": 90,
    "verse_number": 19,
    "content": "اور جنہوں نے ہماری آیتوں کو نہ مانا وہ بدبخت ہیں"
  },
  {
    "surah_number": 90,
    "verse_number": 20,
    "content": "یہ لوگ آگ میں بند کر دیئے جائیں گے"
  },
  {
    "surah_number": 91,
    "verse_number": 1,
    "content": "سورج کی قسم اور اس کی روشنی کی"
  },
  {
    "surah_number": 91,
    "verse_number": 2,
    "content": "اور چاند کی جب اس کے پیچھے نکلے"
  },
  {
    "surah_number": 91,
    "verse_number": 3,
    "content": "اور دن کی جب اُسے چمکا دے"
  },
  {
    "surah_number": 91,
    "verse_number": 4,
    "content": "اور رات کی جب اُسے چھپا لے"
  },
  {
    "surah_number": 91,
    "verse_number": 5,
    "content": "اور آسمان کی اور اس ذات کی جس نے اسے بنایا"
  },
  {
    "surah_number": 91,
    "verse_number": 6,
    "content": "اور زمین کی اور اس کی جس نے اسے پھیلایا"
  },
  {
    "surah_number": 91,
    "verse_number": 7,
    "content": "اور انسان کی اور اس کی جس نے اس (کے اعضا) کو برابر کیا"
  },
  {
    "surah_number": 91,
    "verse_number": 8,
    "content": "پھر اس کو بدکاری (سے بچنے) اور پرہیزگاری کرنے کی سمجھ دی"
  },
  {
    "surah_number": 91,
    "verse_number": 9,
    "content": "کہ جس نے (اپنے) نفس (یعنی روح) کو پاک رکھا وہ مراد کو پہنچا"
  },
  {
    "surah_number": 91,
    "verse_number": 10,
    "content": "اور جس نے اسے خاک میں ملایا وہ خسارے میں رہا"
  },
  {
    "surah_number": 91,
    "verse_number": 11,
    "content": "(قوم) ثمود نے اپنی سرکشی کے سبب (پیغمبر کو) جھٹلایا"
  },
  {
    "surah_number": 91,
    "verse_number": 12,
    "content": "جب ان میں سے ایک نہایت بدبخت اٹھا"
  },
  {
    "surah_number": 91,
    "verse_number": 13,
    "content":
        "تو خدا کے پیغمبر (صالح) نے ان سے کہا کہ خدا کی اونٹنی اور اس کے پانی پینے کی باری سے عذر کرو"
  },
  {
    "surah_number": 91,
    "verse_number": 14,
    "content":
        "مگر انہوں نے پیغمبر کو جھٹلایا اور اونٹنی کی کونچیں کاٹ دیں تو خدا نے ان کےگناہ کے سبب ان پر عذاب نازل کیا اور سب کو (ہلاک کر کے) برابر کر دیا"
  },
  {
    "surah_number": 91,
    "verse_number": 15,
    "content": "اور اس کو ان کے بدلہ لینے کا کچھ بھی ڈر نہیں"
  },
  {
    "surah_number": 92,
    "verse_number": 1,
    "content": "رات کی قسم جب (دن کو) چھپالے"
  },
  {
    "surah_number": 92,
    "verse_number": 2,
    "content": "اور دن کی قسم جب چمک اٹھے"
  },
  {
    "surah_number": 92,
    "verse_number": 3,
    "content": "اور اس (ذات) کی قسم جس نے نر اور مادہ پیدا کیے"
  },
  {
    "surah_number": 92,
    "verse_number": 4,
    "content": "کہ تم لوگوں کی کوششں طرح طرح کی ہے"
  },
  {
    "surah_number": 92,
    "verse_number": 5,
    "content": "تو جس نے (خدا کے رستے میں مال) دیا اور پرہیز گاری کی"
  },
  {"surah_number": 92, "verse_number": 6, "content": "اور نیک بات کو سچ جانا"},
  {
    "surah_number": 92,
    "verse_number": 7,
    "content": "اس کو ہم آسان طریقے کی توفیق دیں گے"
  },
  {
    "surah_number": 92,
    "verse_number": 8,
    "content": "اور جس نے بخل کیا اور بےپروا بنا رہا"
  },
  {
    "surah_number": 92,
    "verse_number": 9,
    "content": "اور نیک بات کو جھوٹ سمجھا"
  },
  {
    "surah_number": 92,
    "verse_number": 10,
    "content": "اسے سختی میں پہنچائیں گے"
  },
  {
    "surah_number": 92,
    "verse_number": 11,
    "content":
        "اور جب وہ (دوزخ کے گڑھے میں) گرے گا تو اس کا مال اس کے کچھ کام نہ آئے گا"
  },
  {
    "surah_number": 92,
    "verse_number": 12,
    "content": "ہمیں تو راہ دکھا دینا ہے"
  },
  {
    "surah_number": 92,
    "verse_number": 13,
    "content": "اور آخرت او ردنیا ہماری ہی چیزیں ہیں"
  },
  {
    "surah_number": 92,
    "verse_number": 14,
    "content": "سو میں نے تم کو بھڑکتی آگ سے متنبہ کر دیا"
  },
  {
    "surah_number": 92,
    "verse_number": 15,
    "content": "اس میں وہی داخل ہو گا جو بڑا بدبخت ہے"
  },
  {
    "surah_number": 92,
    "verse_number": 16,
    "content": "جس نے جھٹلایا اور منہ پھیرا"
  },
  {
    "surah_number": 92,
    "verse_number": 17,
    "content": "اور جو بڑا پرہیزگار ہے وہ (اس سے) بچا لیا جائے گا"
  },
  {
    "surah_number": 92,
    "verse_number": 18,
    "content": "جو مال دیتا ہے تاکہ پاک ہو"
  },
  {
    "surah_number": 92,
    "verse_number": 19,
    "content":
        "اور (اس لیے) نہیں (دیتا کہ) اس پر کسی کا احسان (ہے) جس کا وہ بدلہ اتارتا ہے"
  },
  {
    "surah_number": 92,
    "verse_number": 20,
    "content": "بلکہ اپنے خداوند اعلیٰ کی رضامندی حاصل کرنے کے لیے دیتا ہے"
  },
  {
    "surah_number": 92,
    "verse_number": 21,
    "content": "اور وہ عنقریب خوش ہو جائے گا"
  },
  {"surah_number": 93, "verse_number": 1, "content": "آفتاب کی روشنی کی قسم"},
  {
    "surah_number": 93,
    "verse_number": 2,
    "content": "اور رات (کی تاریکی) کی جب چھا جائے"
  },
  {
    "surah_number": 93,
    "verse_number": 3,
    "content":
        "کہ (اے محمدﷺ) تمہارے پروردگار نے نہ تو تم کو چھوڑا اور نہ (تم سے) ناراض ہوا"
  },
  {
    "surah_number": 93,
    "verse_number": 4,
    "content": "اور آخرت تمہارے لیے پہلی (حالت یعنی دنیا) سے کہیں بہتر ہے"
  },
  {
    "surah_number": 93,
    "verse_number": 5,
    "content":
        "اور تمہیں پروردگار عنقریب وہ کچھ عطا فرمائے گا کہ تم خوش ہو جاؤ گے"
  },
  {
    "surah_number": 93,
    "verse_number": 6,
    "content": "بھلا اس نے تمہیں یتیم پا کر جگہ نہیں دی؟ (بےشک دی)"
  },
  {
    "surah_number": 93,
    "verse_number": 7,
    "content": "اور رستے سے ناواقف دیکھا تو رستہ دکھایا"
  },
  {
    "surah_number": 93,
    "verse_number": 8,
    "content": "اور تنگ دست پایا تو غنی کر دیا"
  },
  {
    "surah_number": 93,
    "verse_number": 9,
    "content": "تو تم بھی یتیم پر ستم نہ کرنا"
  },
  {
    "surah_number": 93,
    "verse_number": 10,
    "content": "اور مانگنے والے کو جھڑکی نہ دینا"
  },
  {
    "surah_number": 93,
    "verse_number": 11,
    "content": "اور اپنے پروردگار کی نعمتوں کا بیان کرتے رہنا"
  },
  {
    "surah_number": 94,
    "verse_number": 1,
    "content": "(اے محمدﷺ) کیا ہم نے تمہارا سینہ کھول نہیں دیا؟ (بےشک کھول دیا)"
  },
  {
    "surah_number": 94,
    "verse_number": 2,
    "content": "اور تم پر سے بوجھ بھی اتار دیا"
  },
  {
    "surah_number": 94,
    "verse_number": 3,
    "content": "جس نے تمہاری پیٹھ توڑ رکھی تھی"
  },
  {"surah_number": 94, "verse_number": 4, "content": "اور تمہارا ذکر بلند کیا"},
  {
    "surah_number": 94,
    "verse_number": 5,
    "content": "ہاں ہاں مشکل کے ساتھ آسانی بھی ہے"
  },
  {
    "surah_number": 94,
    "verse_number": 6,
    "content": "(اور) بے شک مشکل کے ساتھ آسانی ہے"
  },
  {
    "surah_number": 94,
    "verse_number": 7,
    "content": "تو جب فارغ ہوا کرو تو (عبادت میں) محنت کیا کرو"
  },
  {
    "surah_number": 94,
    "verse_number": 8,
    "content": "اور اپنے پروردگار کی طرف متوجہ ہو جایا کرو"
  },
  {
    "surah_number": 95,
    "verse_number": 1,
    "content": "انجیر کی قسم اور زیتون کی"
  },
  {"surah_number": 95, "verse_number": 2, "content": "اور طور سینین کی"},
  {"surah_number": 95, "verse_number": 3, "content": "اور اس امن والے شہر کی"},
  {
    "surah_number": 95,
    "verse_number": 4,
    "content": "کہ ہم نے انسان کو بہت اچھی صورت میں پیدا کیا ہے"
  },
  {
    "surah_number": 95,
    "verse_number": 5,
    "content": "پھر (رفتہ رفتہ) اس (کی حالت) کو (بدل کر) پست سے پست کر دیا"
  },
  {
    "surah_number": 95,
    "verse_number": 6,
    "content":
        "مگر جو لوگ ایمان لائے اور نیک عمل کرتے رہے انکے لیے بےانتہا اجر ہے"
  },
  {
    "surah_number": 95,
    "verse_number": 7,
    "content": "تو (اے آدم زاد) پھر تو جزا کے دن کو کیوں جھٹلاتا ہے؟"
  },
  {
    "surah_number": 95,
    "verse_number": 8,
    "content": "کیا خدا سب سے بڑا حاکم نہیں ہے؟"
  },
  {
    "surah_number": 96,
    "verse_number": 1,
    "content":
        "(اے محمدﷺ) اپنے پروردگار کا نام لے کر پڑھو جس نے (عالم کو) پیدا کیا"
  },
  {
    "surah_number": 96,
    "verse_number": 2,
    "content": "جس نے انسان کو خون کی پھٹکی سے بنایا"
  },
  {
    "surah_number": 96,
    "verse_number": 3,
    "content": "پڑھو اور تمہارا پروردگار بڑا کریم ہے"
  },
  {
    "surah_number": 96,
    "verse_number": 4,
    "content": "جس نے قلم کے ذریعے سے علم سکھایا"
  },
  {
    "surah_number": 96,
    "verse_number": 5,
    "content": "اور انسان کو وہ باتیں سکھائیں جس کا اس کو علم نہ تھا"
  },
  {
    "surah_number": 96,
    "verse_number": 6,
    "content": "مگر انسان سرکش ہو جاتا ہے"
  },
  {
    "surah_number": 96,
    "verse_number": 7,
    "content": "جب کہ اپنے تیئں غنی دیکھتا ہے"
  },
  {
    "surah_number": 96,
    "verse_number": 8,
    "content": "کچھ شک نہیں کہ (اس کو) تمہارے پروردگار ہی کی طرف لوٹ کر جانا ہے"
  },
  {
    "surah_number": 96,
    "verse_number": 9,
    "content": "بھلا تم نے اس شخص کو دیکھا جو منع کرتا ہے"
  },
  {
    "surah_number": 96,
    "verse_number": 10,
    "content": "(یعنی) ایک بندے کو جب وہ نماز پڑھنے لگتا ہے"
  },
  {
    "surah_number": 96,
    "verse_number": 11,
    "content": "بھلا دیکھو تو اگر یہ راہِ راست پر ہو"
  },
  {
    "surah_number": 96,
    "verse_number": 12,
    "content": "یا پرہیز گاری کا حکم کرے (تو منع کرنا کیسا)"
  },
  {
    "surah_number": 96,
    "verse_number": 13,
    "content":
        "اور دیکھو تو اگر اس نے دین حق کو جھٹلایا اور اس سے منہ موڑا (تو کیا ہوا)"
  },
  {
    "surah_number": 96,
    "verse_number": 14,
    "content": "کیااس کو معلوم نہیں کہ خدا دیکھ رہا ہے"
  },
  {
    "surah_number": 96,
    "verse_number": 15,
    "content":
        "دیکھو اگر وہ باز نہ آئے گا تو ہم (اس کی) پیشانی کے بال پکڑ گھسیٹیں گے"
  },
  {
    "surah_number": 96,
    "verse_number": 16,
    "content": "یعنی اس جھوٹے خطاکار کی پیشانی کے بال"
  },
  {
    "surah_number": 96,
    "verse_number": 17,
    "content": "تو وہ اپنے یاروں کی مجلس کو بلالے"
  },
  {
    "surah_number": 96,
    "verse_number": 18,
    "content": "ہم بھی اپنے موکلانِ دوزخ کو بلا لیں گے"
  },
  {
    "surah_number": 96,
    "verse_number": 19,
    "content": "دیکھو اس کا کہا نہ ماننا اور قربِ (خدا) حاصل کرتے رہنا"
  },
  {
    "surah_number": 97,
    "verse_number": 1,
    "content": "ہم نے اس (قرآن) کو شب قدر میں نازل (کرنا شروع) کیا"
  },
  {
    "surah_number": 97,
    "verse_number": 2,
    "content": "اور تمہیں کیا معلوم کہ شب قدر کیا ہے؟"
  },
  {
    "surah_number": 97,
    "verse_number": 3,
    "content": "شب قدر ہزار مہینے سے بہتر ہے"
  },
  {
    "surah_number": 97,
    "verse_number": 4,
    "content":
        "اس میں روح (الامین) اور فرشتے ہر کام کے (انتظام کے) لیے اپنے پروردگار کے حکم سے اترتے ہیں"
  },
  {
    "surah_number": 97,
    "verse_number": 5,
    "content": "یہ (رات) طلوع صبح تک (امان اور) سلامتی ہے"
  },
  {
    "surah_number": 98,
    "verse_number": 1,
    "content":
        "جو لوگ کافر ہیں (یعنی) اہل کتاب اور مشرک وہ (کفر سے) باز رہنے والے نہ تھے جب تک ان کے پاس کھلی دلیل (نہ) آتی"
  },
  {
    "surah_number": 98,
    "verse_number": 2,
    "content": "(یعنی) خدا کے پیغمبر جو پاک اوراق پڑھتے ہیں"
  },
  {
    "surah_number": 98,
    "verse_number": 3,
    "content": "جن میں (مستحکم) آیتیں لکھی ہوئی ہیں"
  },
  {
    "surah_number": 98,
    "verse_number": 4,
    "content":
        "اور اہل کتاب جو متفرق (و مختلف) ہوئے ہیں تو دلیل واضح آنے کے بعد (ہوئے ہیں)"
  },
  {
    "surah_number": 98,
    "verse_number": 5,
    "content":
        "اور ان کو حکم تو یہی ہوا تھا کہ اخلاص عمل کے ساتھ خدا کی عبادت کریں (اور) یکسو ہو کراورنماز پڑھیں اور زکوٰة دیں اور یہی سچا دین ہے"
  },
  {
    "surah_number": 98,
    "verse_number": 6,
    "content":
        "جو لوگ کافر ہیں (یعنی) اہل کتاب اور مشرک وہ دوزخ کی آگ میں پڑیں گے (اور) ہمیشہ اس میں رہیں گے۔ یہ لوگ سب مخلوق سے بدتر ہیں"
  },
  {
    "surah_number": 98,
    "verse_number": 7,
    "content":
        "(اور) جو لوگ ایمان لائے اور نیک عمل کرتے رہے وہ تمام خلقت سے بہتر ہیں"
  },
  {
    "surah_number": 98,
    "verse_number": 8,
    "content":
        "ان کا صلہ ان کے پروردگار کے ہاں ہمیشہ رہنے کے باغ ہیں جن کے نیچے نہریں بہہ رہی ہیں ابدالاباد ان میں رہیں گے۔ خدا ان سے خوش اور وہ اس سے خوش۔ یہ (صلہ) اس کے لیے ہے جو اپنے پروردگار سے ڈرتا رہا"
  },
  {
    "surah_number": 99,
    "verse_number": 1,
    "content": "جب زمین بھونچال سے ہلا دی جائے گی"
  },
  {
    "surah_number": 99,
    "verse_number": 2,
    "content": "اور زمین اپنے (اندر) کے بوجھ نکال ڈالے گی"
  },
  {
    "surah_number": 99,
    "verse_number": 3,
    "content": "اور انسان کہے گا کہ اس کو کیا ہوا ہے؟"
  },
  {
    "surah_number": 99,
    "verse_number": 4,
    "content": "اس روز وہ اپنے حالات بیان کردے گی"
  },
  {
    "surah_number": 99,
    "verse_number": 5,
    "content": "کیونکہ تمہارے پروردگار نے اس کو حکم بھیجا (ہوگا)"
  },
  {
    "surah_number": 99,
    "verse_number": 6,
    "content":
        "اس دن لوگ گروہ گروہ ہو کر آئیں گے تاکہ ان کو ان کے اعمال دکھا دیئے جائیں"
  },
  {
    "surah_number": 99,
    "verse_number": 7,
    "content": "تو جس نے ذرہ بھر نیکی کی ہو گی وہ اس کو دیکھ لے گا"
  },
  {
    "surah_number": 99,
    "verse_number": 8,
    "content": "اور جس نے ذرہ بھر برائی کی ہوگی وہ اسے دیکھ لے گا"
  },
  {
    "surah_number": 100,
    "verse_number": 1,
    "content": "ان سرپٹ دوڑنے والے گھوڑوں کی قسم جو ہانپ اٹھتےہیں"
  },
  {
    "surah_number": 100,
    "verse_number": 2,
    "content": "پھر (پتھروں پر نعل) مار کر آگ نکالتے ہیں"
  },
  {
    "surah_number": 100,
    "verse_number": 3,
    "content": "پھر صبح کو چھاپہ مارتے ہیں"
  },
  {
    "surah_number": 100,
    "verse_number": 4,
    "content": "پھر اس میں گرد اٹھاتے ہیں"
  },
  {
    "surah_number": 100,
    "verse_number": 5,
    "content": "پھر اس وقت دشمن کی فوج میں جا گھستے ہیں"
  },
  {
    "surah_number": 100,
    "verse_number": 6,
    "content": "کہ انسان اپنے پروردگار کا احسان ناشناس (اور ناشکرا) ہے"
  },
  {
    "surah_number": 100,
    "verse_number": 7,
    "content": "اور وہ اس سے آگاہ بھی ہے"
  },
  {
    "surah_number": 100,
    "verse_number": 8,
    "content": "وہ تو مال سے سخت محبت کرنے والا ہے"
  },
  {
    "surah_number": 100,
    "verse_number": 9,
    "content":
        "کیا وہ اس وقت کو نہیں جانتا کہ جو (مردے) قبروں میں ہیں وہ باہر نکال لیے جائیں گے"
  },
  {
    "surah_number": 100,
    "verse_number": 10,
    "content": "اور جو (بھید) دلوں میں ہیں وہ ظاہر کر دیئے جائیں گے"
  },
  {
    "surah_number": 100,
    "verse_number": 11,
    "content": "بےشک ان کا پروردگار اس روز ان سے خوب واقف ہوگا"
  },
  {"surah_number": 101, "verse_number": 1, "content": "کھڑ کھڑانے والی"},
  {
    "surah_number": 101,
    "verse_number": 2,
    "content": "کھڑ کھڑانے والی کیا ہے؟"
  },
  {
    "surah_number": 101,
    "verse_number": 3,
    "content": "اور تم کیا جانوں کھڑ کھڑانے والی کیا ہے؟"
  },
  {
    "surah_number": 101,
    "verse_number": 4,
    "content": "(وہ قیامت ہے) جس دن لوگ ایسے ہوں گے جیسے بکھرے ہوئے پتنگے"
  },
  {
    "surah_number": 101,
    "verse_number": 5,
    "content": "اور پہاڑ ایسے ہو جائیں گے جیسے دھنکی ہوئی رنگ برنگ کی اون"
  },
  {
    "surah_number": 101,
    "verse_number": 6,
    "content": "تو جس کے (اعمال کے) وزن بھاری نکلیں گے"
  },
  {
    "surah_number": 101,
    "verse_number": 7,
    "content": "وہ دل پسند عیش میں ہو گا"
  },
  {
    "surah_number": 101,
    "verse_number": 8,
    "content": "اور جس کے وزن ہلکے نکلیں گے"
  },
  {"surah_number": 101, "verse_number": 9, "content": "اس کا مرجع ہاویہ ہے"},
  {
    "surah_number": 101,
    "verse_number": 10,
    "content": "اور تم کیا سمجھے کہ ہاویہ کیا چیز ہے؟"
  },
  {"surah_number": 101, "verse_number": 11, "content": "دہکتی ہوئی آگ ہے"},
  {
    "surah_number": 102,
    "verse_number": 1,
    "content": "(لوگو) تم کو (مال کی) بہت سی طلب نے غافل کر دیا"
  },
  {
    "surah_number": 102,
    "verse_number": 2,
    "content": "یہاں تک کہ تم نے قبریں جا دیکھیں"
  },
  {
    "surah_number": 102,
    "verse_number": 3,
    "content": "دیکھو تمہیں عنقریب معلوم ہو جائے گا"
  },
  {
    "surah_number": 102,
    "verse_number": 4,
    "content": "پھر دیکھو تمہیں عنقریب معلوم ہو جائے گا"
  },
  {
    "surah_number": 102,
    "verse_number": 5,
    "content": "دیکھو اگر تم جانتے (یعنی) علم الیقین (رکھتے تو غفلت نہ کرتے)"
  },
  {
    "surah_number": 102,
    "verse_number": 6,
    "content": "تم ضرور دوزخ کو دیکھو گے"
  },
  {
    "surah_number": 102,
    "verse_number": 7,
    "content": "پھر اس کو (ایسا) دیکھو گے (کہ) عین الیقین (آ جائے گا)"
  },
  {
    "surah_number": 102,
    "verse_number": 8,
    "content": "پھر اس روز تم سے (شکر) نعمت کے بارے میں پرسش ہو گی"
  },
  {"surah_number": 103, "verse_number": 1, "content": "عصر کی قسم"},
  {"surah_number": 103, "verse_number": 2, "content": "کہ انسان نقصان میں ہے"},
  {
    "surah_number": 103,
    "verse_number": 3,
    "content":
        "مگر وہ لوگ جو ایمان لائے اور نیک عمل کرتے رہے اور آپس میں حق (بات) کی تلقین اور صبر کی تاکید کرتے رہے"
  },
  {
    "surah_number": 104,
    "verse_number": 1,
    "content": "ہر طعن آمیز اشارتیں کرنے والے چغل خور کی خرابی ہے"
  },
  {
    "surah_number": 104,
    "verse_number": 2,
    "content": "جو مال جمع کرتا اور اس کو گن گن کر رکھتا ہے"
  },
  {
    "surah_number": 104,
    "verse_number": 3,
    "content":
        "(اور) خیال کرتا ہے کہ اس کا مال اس کی ہمیشہ کی زندگی کا موجب ہو گا"
  },
  {
    "surah_number": 104,
    "verse_number": 4,
    "content": "ہر گز نہیں وہ ضرور حطمہ میں ڈالا جائے گا"
  },
  {
    "surah_number": 104,
    "verse_number": 5,
    "content": "اور تم کیا سمجھے حطمہ کیا ہے؟"
  },
  {
    "surah_number": 104,
    "verse_number": 6,
    "content": "وہ خدا کی بھڑکائی ہوئی آگ ہے"
  },
  {"surah_number": 104, "verse_number": 7, "content": "جو دلوں پر جا لپٹے گی"},
  {
    "surah_number": 104,
    "verse_number": 8,
    "content": "(اور) وہ اس میں بند کر دیئے جائیں گے"
  },
  {
    "surah_number": 104,
    "verse_number": 9,
    "content": "(یعنی آگ کے) لمبے لمبے ستونوں میں"
  },
  {
    "surah_number": 105,
    "verse_number": 1,
    "content":
        "کیا تم نے نہیں دیکھا کہ تمہارے پروردگار نے ہاتھی والوں کے ساتھ کیا کیا"
  },
  {
    "surah_number": 105,
    "verse_number": 2,
    "content": "کیا ان کا داؤں غلط نہیں کیا؟ (گیا)"
  },
  {
    "surah_number": 105,
    "verse_number": 3,
    "content": "اور ان پر جھلڑ کے جھلڑ جانور بھیجے"
  },
  {
    "surah_number": 105,
    "verse_number": 4,
    "content": "جو ان پر کھنگر کی پتھریاں پھینکتے تھے"
  },
  {
    "surah_number": 105,
    "verse_number": 5,
    "content": "تو ان کو ایسا کر دیا جیسے کھایا ہوا بھس"
  },
  {
    "surah_number": 106,
    "verse_number": 1,
    "content": "قریش کے مانوس کرنے کے سبب"
  },
  {
    "surah_number": 106,
    "verse_number": 2,
    "content": "(یعنی) ان کو جاڑے اور گرمی کے سفر سے مانوس کرنے کے سبب"
  },
  {
    "surah_number": 106,
    "verse_number": 3,
    "content":
        "لوگوں کو چاہیئے کہ (اس نعمت کے شکر میں) اس گھر کے مالک کی عبادت کریں"
  },
  {
    "surah_number": 106,
    "verse_number": 4,
    "content": "جس نے ان کو بھوک میں کھانا کھلایا اور خوف سے امن بخشا"
  },
  {
    "surah_number": 107,
    "verse_number": 1,
    "content": "بھلا تم نے اس شخص کو دیکھا جو (روزِ) جزا کو جھٹلاتا ہے؟"
  },
  {
    "surah_number": 107,
    "verse_number": 2,
    "content": "یہ وہی (بدبخت) ہے، جو یتیم کو دھکے دیتا ہے"
  },
  {
    "surah_number": 107,
    "verse_number": 3,
    "content": "اور فقیر کو کھانا کھلانے کے لیے (لوگوں کو) ترغیب نہیں دیتا"
  },
  {
    "surah_number": 107,
    "verse_number": 4,
    "content": "تو ایسے نمازیوں کی خرابی ہے"
  },
  {
    "surah_number": 107,
    "verse_number": 5,
    "content": "جو نماز کی طرف سے غافل رہتے ہیں"
  },
  {"surah_number": 107, "verse_number": 6, "content": "جو ریا کاری کرتے ہیں"},
  {
    "surah_number": 107,
    "verse_number": 7,
    "content": "اور برتنے کی چیزیں عاریتہً نہیں دیتے"
  },
  {
    "surah_number": 108,
    "verse_number": 1,
    "content": "(اے محمدﷺ) ہم نے تم کو کوثر عطا فرمائی ہے"
  },
  {
    "surah_number": 108,
    "verse_number": 2,
    "content": "تو اپنے پروردگار کے لیے نماز پڑھا کرو اور قربانی دیا کرو"
  },
  {
    "surah_number": 108,
    "verse_number": 3,
    "content": "کچھ شک نہیں کہ تمہارا دشمن ہی بےاولاد رہے گا"
  },
  {
    "surah_number": 109,
    "verse_number": 1,
    "content": "(اے پیغمبر ان منکران اسلام سے) کہہ دو کہ اے کافرو!"
  },
  {
    "surah_number": 109,
    "verse_number": 2,
    "content": "جن (بتوں) کو تم پوچتے ہو ان کو میں نہیں پوجتا"
  },
  {
    "surah_number": 109,
    "verse_number": 3,
    "content": "اور جس (خدا) کی میں عبادت کرتا ہوں اس کی تم عبادت نہیں کرتے"
  },
  {
    "surah_number": 109,
    "verse_number": 4,
    "content":
        "اور (میں پھر کہتا ہوں کہ) جن کی تم پرستش کرتے ہوں ان کی میں پرستش کرنے والا نہیں ہوں"
  },
  {
    "surah_number": 109,
    "verse_number": 5,
    "content":
        "اور نہ تم اس کی بندگی کرنے والے (معلوم ہوتے) ہو جس کی میں بندگی کرتا ہوں"
  },
  {
    "surah_number": 109,
    "verse_number": 6,
    "content": "تم اپنے دین پر میں اپنے دین پر"
  },
  {
    "surah_number": 110,
    "verse_number": 1,
    "content": "جب خدا کی مدد آ پہنچی اور فتح (حاصل ہو گئی)"
  },
  {
    "surah_number": 110,
    "verse_number": 2,
    "content":
        "اور تم نے دیکھ لیا کہ لوگ غول کے غول خدا کے دین میں داخل ہو رہے ہیں"
  },
  {
    "surah_number": 110,
    "verse_number": 3,
    "content":
        "تو اپنے پروردگار کی تعریف کے ساتھ تسبیح کرو اور اس سے مغفرت مانگو، بے شک وہ معاف کرنے والا ہے"
  },
  {
    "surah_number": 111,
    "verse_number": 1,
    "content": "ابولہب کے ہاتھ ٹوٹیں اور وہ ہلاک ہو"
  },
  {
    "surah_number": 111,
    "verse_number": 2,
    "content": "نہ تو اس کا مال ہی اس کے کچھ کام آیا اور نہ وہ جو اس نے کمایا"
  },
  {
    "surah_number": 111,
    "verse_number": 3,
    "content": "وہ جلد بھڑکتی ہوئی آگ میں داخل ہو گا"
  },
  {
    "surah_number": 111,
    "verse_number": 4,
    "content": "اور اس کی جورو بھی جو ایندھن سر پر اٹھائے پھرتی ہے"
  },
  {
    "surah_number": 111,
    "verse_number": 5,
    "content": "اس کے گلے میں مونج کی رسّی ہو گی"
  },
  {
    "surah_number": 112,
    "verse_number": 1,
    "content": "کہو کہ وہ (ذات پاک جس کا نام) الله (ہے) ایک ہے"
  },
  {
    "surah_number": 112,
    "verse_number": 2,
    "content": "معبود برحق جو بےنیاز ہے"
  },
  {
    "surah_number": 112,
    "verse_number": 3,
    "content": "نہ کسی کا باپ ہے اور نہ کسی کا بیٹا"
  },
  {
    "surah_number": 112,
    "verse_number": 4,
    "content": "اور کوئی اس کا ہمسر نہیں"
  },
  {
    "surah_number": 113,
    "verse_number": 1,
    "content": "کہو کہ میں صبح کے پروردگار کی پناہ مانگتا ہوں"
  },
  {
    "surah_number": 113,
    "verse_number": 2,
    "content": "ہر چیز کی بدی سے جو اس نے پیدا کی"
  },
  {
    "surah_number": 113,
    "verse_number": 3,
    "content": "اور شب تاریکی کی برائی سے جب اس کااندھیرا چھا جائے"
  },
  {
    "surah_number": 113,
    "verse_number": 4,
    "content": "اور گنڈوں پر (پڑھ پڑھ کر) پھونکنے والیوں کی برائی سے"
  },
  {
    "surah_number": 113,
    "verse_number": 5,
    "content": "اور حسد کرنے والے کی برائی سے جب حسد کرنے لگے"
  },
  {
    "surah_number": 114,
    "verse_number": 1,
    "content": "کہو کہ میں لوگوں کے پروردگار کی پناہ مانگتا ہوں"
  },
  {
    "surah_number": 114,
    "verse_number": 2,
    "content": "(یعنی) لوگوں کے حقیقی بادشاہ کی"
  },
  {"surah_number": 114, "verse_number": 3, "content": "لوگوں کے معبود برحق کی"},
  {
    "surah_number": 114,
    "verse_number": 4,
    "content":
        "(شیطان) وسوسہ انداز کی برائی سے جو (خدا کا نام سن کر) پیچھے ہٹ جاتا ہے"
  },
  {
    "surah_number": 114,
    "verse_number": 5,
    "content": "جو لوگوں کے دلوں میں وسوسے ڈالتا ہے"
  },
  {
    "surah_number": 114,
    "verse_number": 6,
    "content": "وہ جنّات میں سے (ہو) یا انسانوں میں سے"
  },
];
